import { FlowLaunchList, Delete } from '@/api/workFlow/FlowLaunch'
import { FlowEngineListAll, getFlowList } from '@/api/workFlow/FlowEngine'
import FlowBox from '../components/FlowBox'
import flow from './Flow'
export default {
  name: 'workFlow-flowLaunch',
  components: { FlowBox, flow },
  data() {
    return {
      list: [],
      total: 0,
      showAll: false,
      listLoading: false,
      listQuery: {
        currentPage: 1,
        pageSize: 20,
        sort: 'desc',
        sidx: ''
      },
      formVisible: false,
      flowVisible: false,
      pickerOptions: {
        shortcuts: [{
          text: '最近一周',
          onClick(picker) {
            const end = new Date();
            const start = new Date();
            start.setTime(start.getTime() - 3600 * 1000 * 24 * 7);
            picker.$emit('pick', [start, end]);
          }
        }, {
          text: '最近一个月',
          onClick(picker) {
            const end = new Date();
            const start = new Date();
            start.setTime(start.getTime() - 3600 * 1000 * 24 * 30);
            picker.$emit('pick', [start, end]);
          }
        }, {
          text: '最近三个月',
          onClick(picker) {
            const end = new Date();
            const start = new Date();
            start.setTime(start.getTime() - 3600 * 1000 * 24 * 90);
            picker.$emit('pick', [start, end]);
          }
        }]
      },
      statusList: [{
        id: 0,
        fullName: '等待提交'
      }, {
        id: 1,
        fullName: '等待审核'
      }, {
        id: 2,
        fullName: '审核通过'
      }, {
        id: 3,
        fullName: '审核退回'
      }, {
        id: 4,
        fullName: '流程撤回'
      }, {
        id: 5,
        fullName: '审核终止'
      }, {
        id: 6,
        fullName: '已被挂起'
      }],
      urgentList: [
        {
          id: 1,
          fullName: '普通'
        }, {
          id: 2,
          fullName: '重要'
        }, {
          id: 3,
          fullName: '紧急'
        }
      ],
      keyword: '',
      pickerVal: [],
      startTime: '',
      endTime: '',
      templateId: '',
      flowId: '',
      status: '',
      urgent: '',
      flowCategory: '',
      categoryList: [],
      flowEngineList: [],
      flowOptions: []
    }
  },
  filters: {
    getCategoryText(id, categoryList) {
      let item = categoryList.filter(o => o.enCode == id)[0]
      return item && item.fullName ? item.fullName : ''
    }
  },
  created() {
    this.getDictionaryData()
    this.getFlowEngineList()
    this.initData()
  },
  methods: {
    search() {
      if (this.pickerVal && this.pickerVal.length) {
        this.startTime = this.pickerVal[0]
        this.endTime = this.pickerVal[1]
      } else {
        this.startTime = ''
        this.endTime = ''
      }
      this.listQuery = {
        currentPage: 1,
        pageSize: 20,
        sort: 'desc',
        sidx: ''
      }
      this.initData()
    },
    getFlowEngineList() {
      FlowEngineListAll().then((res) => {
        this.flowEngineList = res.data.list
      })
    },
    getDictionaryData() {
      this.$store.dispatch('base/getDictionaryData', { sort: 'WorkFlowCategory' }).then((res) => {
        this.categoryList = res
      })
    },
    initData() {
      this.listLoading = true
      let query = {
        ...this.listQuery,
        keyword: this.keyword,
        startTime: this.startTime,
        endTime: this.endTime,
        templateId: this.templateId,
        flowId: this.flowId,
        status: this.status,
        flowUrgent: this.urgent,
        flowCategory: this.flowCategory
      }
      FlowLaunchList(query).then(res => {
        this.list = res.data.list
        this.total = res.data.pagination.total
        this.listLoading = false
      })
    },
    handleDel(index, id) {
      this.$confirm(this.$t('common.delTip'), this.$t('common.tipTitle'), {
        type: 'warning'
      }).then(() => {
        this.asyncDel(index, id);
      }).catch(() => { });
    },
    asyncDel(index, id) {
      Delete(id).then(res => {
        this.list.splice(index, 1);
        this.$message({
          type: 'success',
          message: res.msg
        });
      })
    },
    addFlow() {
      this.flowVisible = true
      this.$nextTick(() => {
        this.$refs.flow.init()
      })
    },
    choiceFlow(item) {
      let data = {
        id: '',
        flowId: item.id,
        opType: '-1'
      }
      this.formVisible = true
      this.$nextTick(() => {
        this.$refs.FlowBox.init(data)
        this.flowVisible = false
      })
    },
    toDetail(item, opType) {
      let data = {
        id: item.id,
        flowId: item.flowId,
        opType,
        parentId: item.parentId
      }
      this.formVisible = true
      this.$nextTick(() => {
        this.$refs.FlowBox.init(data)
      })
    },
    closeForm(isRefresh) {
      this.formVisible = false
      if (isRefresh) this.refresh()
    },
    refresh() {
      this.pickerVal = ''
      this.startTime = ''
      this.endTime = ''
      this.keyword = ''
      this.templateId = ''
      this.flowId = ''
      this.status = ''
      this.urgent = ''
      this.flowOptions = []
      this.flowCategory = ''
      this.listQuery = {
        currentPage: 1,
        pageSize: 20,
        sort: 'desc',
        sidx: ''
      }
      this.initData()
    },
    onTemplateIdChange(val) {
      this.flowId = ''
      this.flowOptions = []
      if (!val) return
      this.getFlowList()
    },
    getFlowList() {
      getFlowList(this.templateId).then(res => {
        this.flowOptions = res.data
      })
    },
    visibleFlowChange(val) {
      if (!val) return
      if (!this.templateId) this.$message.warning('请先选择所属流程')
    },
  }
}
