import Process from '@/components/Process/Preview'
import { getFormById } from '@/api/workFlow/FormDesign'
import { flowJsonList, mainVersion, delVersion, getFlowList } from '@/api/workFlow/FlowEngine'
export default {
  components: { Process },
  props: [],
  data() {
    return {
      flowVisible: false,
      id: '',
      title: '',
      list: [],
      total: 0,
      listLoading: false,
      btnLoading: false,
      listQuery: {
        currentPage: 1,
        pageSize: 20,
        sort: 'desc',
        sidx: ''
      },
      startTime: '',
      endTime: '',
      keyword: '',
      flowId: '',
      enCode: '',
      pickerVal: '',
      flowTemplateJson: {},
      pickerOptions: {
        shortcuts: [{
          text: '最近一周',
          onClick(picker) {
            const end = new Date();
            const start = new Date();
            start.setTime(start.getTime() - 3600 * 1000 * 24 * 7);
            picker.$emit('pick', [start, end]);
          }
        }, {
          text: '最近一个月',
          onClick(picker) {
            const end = new Date();
            const start = new Date();
            start.setTime(start.getTime() - 3600 * 1000 * 24 * 30);
            picker.$emit('pick', [start, end]);
          }
        }, {
          text: '最近三个月',
          onClick(picker) {
            const end = new Date();
            const start = new Date();
            start.setTime(start.getTime() - 3600 * 1000 * 24 * 90);
            picker.$emit('pick', [start, end]);
          }
        }]
      },
      templateList: []
    }
  },
  methods: {
    updateRelease(item) {
      this.$confirm('启用后，新发起的流程将按此版本流转。未审批完的流程，按原来的版本流转。', '系统提示', {
        type: 'warning'
      }).then(() => {
        mainVersion(item.id).then(res => {
          if (res.code == 200) {
            this.$message({
              type: 'success',
              message: res.msg
            });
            this.initData()
          }
        })
      }).catch(() => { });
    },
    del(item) {
      this.$confirm('此操作将永久删除该数据，是否继续？', '系统提示', {
        type: 'warning'
      }).then(() => {
        delVersion(item.id).then(res => {
          if (res.code == 200) {
            this.$message({
              type: 'success',
              message: res.msg
            });
            this.initData()
          }
        })
      })
    },
    flowInfo(item) {
      this.flowVisible = true
      this.flowTemplateJson = item.flowTemplateJson ? JSON.parse(item.flowTemplateJson) : null
    },
    goBack() {
      if (this.flowVisible) {
        return this.flowVisible = false
      }
      this.$emit('close', true)
    },
    init(id, fullName, type) {
      this.title = fullName
      this.listLoading = true
      if (type) {
        this.id = id
        getFlowList(this.id).then(res => {
          this.templateList = res.data
          if (!this.templateList.length) {
            this.$message({
              type: 'error',
              message: '流程未设计，请先设计流程'
            });
            return this.listLoading = false
          }
          this.flowId = this.templateList[0].id
          this.initData()
        }).catch(() => { this.listLoading = false });
      } else {
        getFormById(id).then(data => {
          this.id = data.data && data.data.id
          getFlowList(this.id).then(res => {
            this.templateList = res.data
            if (!this.templateList.length) {
              this.$message({
                type: 'error',
                message: '流程未设计，请先设计流程'
              });
              return this.listLoading = false
            }
            this.flowId = this.templateList[0].id
            this.initData()
          }).catch(() => { this.listLoading = false });
        }).catch(() => { this.listLoading = false });
      }
    },
    reset() {
      this.pickerVal = ''
      this.startTime = ''
      this.endTime = ''
      this.keyword = ''
      this.listQuery = {
        currentPage: 1,
        pageSize: 20,
        sort: 'desc',
        sidx: ''
      }
      this.search()
    },
    search() {
      if (this.pickerVal && this.pickerVal.length) {
        this.startTime = this.pickerVal[0]
        this.endTime = this.pickerVal[1]
      } else {
        this.startTime = ''
        this.endTime = ''
      }
      this.listQuery = {
        currentPage: 1,
        pageSize: 20,
        sort: 'desc',
        sidx: ''
      }
      this.initData()
    },
    initData() {
      this.listLoading = true
      let query = {
        ...this.listQuery,
        startTime: this.startTime,
        endTime: this.endTime,
        keyword: this.keyword,
        templateId: this.id,
        flowId: this.flowId
      }
      flowJsonList(this.id, query).then((res) => {
        this.list = res.data.list
        this.total = res.data.pagination.total
        this.listLoading = false
      })
    },
  }
}
