import Form from './Form'
import {
  getConfigList,
  delConfig,
  copyConfig,
  exportConfig
} from '@/api/msgCenter/accountConfig'
export default {
  name: 'msgCenter-accountConfig-shortMsg',
  components: {
    Form
  },
  data() {
    return {
      list: [],
      enabledMarkList: [{ fullName: '启用', id: 1 }, { fullName: '禁用', id: 0 }],
      channelList: [{ fullName: '阿里云', id: 1 }, { fullName: '腾讯云', id: 2 }],
      keyword: '',
      channel: '',
      enabledMark: '',
      listQuery: {
        currentPage: 1,
        pageSize: 20,
        sort: 'desc',
        sidx: ''
      },
      total: 0,
      listLoading: true,
      formVisible: false
    }
  },
  created() {
    this.initData()
  },
  methods: {
    reset() {
      this.keyword = ''
      this.enabledMark = ''
      this.channel = ''
      this.search()
    },
    search() {
      this.listQuery = {
        currentPage: 1,
        pageSize: 20,
        sort: 'desc',
        sidx: ''
      }
      this.initData()
    },
    initData() {
      this.listLoading = true
      let query = {
        ...this.listQuery,
        keyword: this.keyword,
        enabledMark: this.enabledMark,
        channel: this.channel,
        type: 3
      }
      getConfigList(query).then(res => {
        this.list = res.data.list
        this.total = res.data.pagination.total
        this.listLoading = false
      }).catch(() => {
        this.listLoading = false
      })
    },
    addOrUpdateHandle(id) {
      this.formVisible = true
      this.$nextTick(() => {
        this.$refs.Form.init(id, this.channelList)
      })
    },
    copy(id) {
      this.$confirm('您确定要复制该数据, 是否继续?', '提示', {
        type: 'warning'
      }).then(() => {
        copyConfig(id).then(res => {
          this.$message({
            type: 'success',
            message: res.msg,
            duration: 1000,
            onClose: () => {
              this.initData()
            }
          });
        })
      }).catch(() => { });
    },
    exportTpl(id) {
      this.$confirm('您确定要导出该数据, 是否继续?', '提示', {
        type: 'warning'
      }).then(() => {
        exportConfig(id).then(res => {
          this.jnpf.downloadFile(res.data.url)
        })
      }).catch(() => { });
    },
    handleDel(id) {
      this.$confirm(this.$t('common.delTip'), this.$t('common.tipTitle'), {
        type: 'warning'
      }).then(() => {
        delConfig(id).then(res => {
          this.$message({
            type: 'success',
            message: res.msg,
            duration: 1500,
            onClose: () => {
              this.initData()
            }
          })
        })
      }).catch(() => { })
    },
  }
}
