.Profile-container {
  position: relative;
  padding-top: 10px;
  .profile-tabs {
    >>> .el-tabs__nav-scroll {
      padding-top: 70px;
    }
    >>> .el-tabs__item {
      text-align: left !important;
    }
    >>> .el-tabs__content {
      height: 100%;
      .el-tab-pane {
        height: 100%;
        overflow: auto;
        overflow-x: hidden;

        &.el-tab-pane-authorize {
          overflow: hidden;
        }
      }
    }
    >>> #tab-line {
      height: 20px;
      width: 160px;
      &::after {
        position: absolute;
        width: 100%;
        height: 1px;
        background: #ddd;
        content: '';
        display: block;
        overflow: hidden;
        top: 10px;
        left: 0;
      }
    }
  }
  .head {
    position: absolute;
    left: 10px;
    top: 10px;
    height: 70px;
    width: 160px;
    padding-top: 10px;
    >>> .avatar-uploader {
      display: inline-block;
      vertical-align: top;
      .avatar-hover {
        position: absolute;
        left: 0;
        top: 0;
        font-size: 12px;
        display: none;
        overflow: hidden;
        width: 50px;
        height: 50px;
        text-align: center;
        border-radius: 50%;
        line-height: 50px;
        color: #fff;
        cursor: pointer;
        background: rgba(0, 0, 0, 0.5);
      }
      &:hover {
        & .avatar-hover {
          display: block;
        }
      }
    }
    .avatar-box {
      position: relative;
    }
    .avatar {
      display: inline-block;
      width: 50px;
      height: 50px;
      overflow: hidden;
      border-radius: 50%;
      vertical-align: top;
      margin-right: 10px;
    }
    .username {
      line-height: 50px;
      font-size: 14px;
    }
  }

  >>> .el-select,
  >>> .el-date-editor {
    width: 100%;
  }
  .subordinate-tree {
    height: 100%;
    margin: 0;
    .subordinate-tree-node {
      width: 300px;
    }
    >>> .el-tree-node:focus > .el-tree-node__content {
      background-color: #fff;
    }
    >>> .el-tree-node__expand-icon.el-icon-caret-right {
      font-size: 20px;
    }
    >>> .el-tree-node__content {
      height: 80px;
      &:hover {
        background: #fff;
      }
    }
    >>> .el-card__body {
      display: flex;
      padding: 10px 10px;
      align-items: center;
      .el-avatar {
        margin-right: 10px;
        flex-shrink: 0;
      }

      .text {
        font-size: 14px;
        width: calc(100% - 60px);
        p {
          line-height: 25px;
          white-space: nowrap;
          text-overflow: ellipsis;
          overflow: hidden;
          word-break: break-all;
        }
      }
      .user-text {
        color: #999;
        font-size: 12px;
      }
    }
  }
  #pane-subordinate {
    overflow: hidden;
    display: flex;
    flex-direction: column;
    .subordinate-list {
      flex: 1;
      overflow: auto;
    }
  }
  .organize-list {
    width: 100%;
    padding: 50px;
    .organize-item {
      margin-bottom: 30px;
      .organize-item-main {
        height: 70px;
        position: relative;
        border-radius: 4px;
        border: 1px solid #dcdfe6;
        display: flex;
        align-items: center;
        padding: 0 20px;
        cursor: pointer;
        box-shadow: 0 0 6px rgba(0, 0, 0, 0.16);
        color: #606266;
        &.active {
          border: 1px solid #1890ff;
          box-shadow: 0 0 6px rgba(6, 58, 108, 0.26);
          color: #1890ff;
          .btn,
          .icon-checked {
            display: block;
          }
        }
        .icon-ym {
          font-size: 24px;
          margin-right: 10px;
        }
        .organize-name {
          line-height: 24px;
          font-size: 14px;
        }
        .btn {
          display: none;
          position: absolute;
          right: 45px;
          bottom: 7px;
          font-size: 12px;
        }
        .icon-checked {
          display: none;
          width: 20px;
          height: 20px;
          border: 20px solid #1890ff;
          border-left: 20px solid transparent;
          border-top: 20px solid transparent;
          border-bottom-right-radius: 2px;
          position: absolute;
          transform: scale(0.9);
          right: -2px;
          bottom: -2px;
          i {
            position: absolute;
            top: -2px;
            left: -3px;
            font-size: 20px;
            color: #fff;
          }
        }
      }
    }
  }
}
