result:
.main >>> a {
  color: #06c !important;
}
.main {
  margin-top: 0px !important;
  >>> .ql-editor {
    white-space: unset;
  }
}
.notice-wrapper {
  .notice-hd {
    padding: 24px 0;
    .title {
      color: #303133;
      margin-top: 0;
      margin-bottom: 8px;
    }
    .info {
      color: #606266;
      padding-bottom: 24px;
      line-height: normal;
    }
  }
  .excerpt {
    color: #303133;
    margin-bottom: 8px !important;
  }
}
.JNPF-messageList {
  .title-box {
    display: flex;
    .title {
      flex: 1;
    }
  }
  .search-input {
    >>> .el-input__inner {
      border-radius: 0;
      border-right: none;
      border-left: none;
    }
    .el-input__icon {
      cursor: pointer;
      &:hover {
        color: #1890ff;
      }
    }
  }
  .is-read-box {
    font-size: 14px;
    color: #999999;
    display: flex;
    align-items: center;

    >>> .el-switch {
      margin-left: 4px;
    }
  }
  .JNPF-messageList-title {
    display: flex;
    align-items: center;
    padding-right: 20px;
    .title {
      font-size: 18px;
      margin-right: 10px;
    }
    .icon-menu {
      font-size: 18px;
      color: #9fafbe;
      cursor: pointer;
    }
  }
  >>> .el-drawer__header {
    border: none;
  }
  >>> .el-drawer__body {
    overflow: hidden;
    display: flex;
    flex-direction: column;
    position: relative;
    .tool {
      height: 32px;
      display: flex;
      justify-content: space-between;
      align-items: center;
      padding: 0 10px;
      background: #fff;
    }
    .JNPF-messageList-box {
      overflow: auto;
      overflow-x: hidden;
      position: relative;
      flex: 1;
      border-top: 1px solid #f5f7f9;
      >>> .el-loading-mask {
        top: 100px;
      }
      .noData-txt {
        font-size: 14px;
        color: #909399;
        line-height: 20px;
        text-align: center;
        padding-top: 20px;
      }
    }
    .JNPF-messageList-item {
      position: relative;
      display: block;
      padding: 0 10px;
      background-color: #fff;
      border-bottom: 1px solid #f5f7f9;
      height: 60px;
      display: flex;
      align-items: center;
      cursor: pointer;
      &:hover {
        background-color: #f5f7f9;
      }
      .flow-icon {
        background-color: #33cc51 !important;
      }
      .notice-icon {
        background-color: #e09f0c !important;
      }
      .schedule-icon {
        background-color: #7777ff !important;
      }
      .JNPF-messageList-item-icon {
        background-color: #1890ff;
        width: 36px;
        height: 36px;
        display: inline-block;
        font-size: 22px;
        color: #fff;
        line-height: 36px;
        border-radius: 50%;
        text-align: center;
      }
      .JNPF-messageList-txt {
        margin-left: 14px;
        overflow: hidden;
        flex: 1;
        padding-top: 1px;
        .title {
          white-space: nowrap;
          text-overflow: ellipsis;
          overflow: hidden;
          font-size: 14px;
          color: #333;
          margin-bottom: 5px;
        }
        .name {
          font-size: 12px;
          color: #999;
          display: flex;
          justify-content: space-between;
          .user {
            flex: 1;
            min-width: 0;
            white-space: nowrap;
            text-overflow: ellipsis;
            overflow: hidden;
          }
        }
      }
    }
    .bottom-box {
      height: 50px;
      display: flex;
      align-items: center;
      justify-content: center;
      font-size: 14px;
      color: #303133;
      cursor: pointer;
      border-top: 1px solid #e3e6eb;

      &:hover {
        color: #1890ff;
      }
    }
  }
}
.main >>> a {
  color: #06c !important;
}
