import { getGoodsList, getProductClassify } from '@/api/extend/saleOrder'
export default {
  data() {
    return {
      defaultProps: {
        children: 'children',
        label: 'fullName'
      },
      treeLoading: false,
      treeData: [],
      visible: false,
      listLoading: true,
      query: {
        classifyId: '',
        code: '',
        fullName: '',
      },
      listQuery: {
        currentPage: 1,
        pageSize: 20,
        sort: 'desc',
        sidx: ''
      },
      list: [],
      total: 0,
      checked: []
    }
  },
  methods: {
    init() {
      this.visible = true
      this.listLoading = true
      this.getProductClassify()
    },
    getProductClassify() {
      this.treeLoading = true
      getProductClassify().then(res => {
        this.treeData = res.data.list
        this.$nextTick(() => {
          this.query.classifyId = this.treeData[0].children[0].id
          this.$refs.treeBox.setCurrentKey(this.query.classifyId)
          this.treeLoading = false
          this.getGoodsList()
        })
      })
    },
    getGoodsList() {
      this.listLoading = true
      const query = {
        ...this.listQuery,
        ...this.query
      }
      getGoodsList(query).then(res => {
        this.list = res.data.list
        this.total = res.data.pagination.total
        this.listLoading = false
      })
    },
    handleNodeClick(data) {
      if (this.query.classifyId === data.id) return
      this.query.classifyId = data.id
      this.reset()
    },
    search() {
      this.listQuery = {
        currentPage: 1,
        pageSize: 20,
        sort: 'desc',
        sidx: ''
      }
      this.getGoodsList()
    },
    refresh() {
      this.getGoodsList()
    },
    select() {
      if (!this.checked.length) return
      this.visible = false
      this.$emit('choice', this.checked)
    },
    handleSelectionChange(val) {
      this.checked = val
    },
    reset() {
      this.query.code = ''
      this.query.fullName = ''
      this.search()
    }
  }
}
