import { getInfo, Update, Create } from '@/api/systemData/commonFields'

export default {
  data() {
    return {
      visible: false,
      dataForm: {
        id: '',
        field: '',
        fieldName: '',
        dataType: '',
        dataLength: 50,
        allowNull: 1
      },
      formLoading: false,
      btnLoading: false,
      options: [
        { label: '字符串', value: 'varchar' },
        { label: '整型', value: 'int' },
        { label: '日期时间', value: 'datetime' },
        { label: '浮点', value: 'decimal' },
        { label: '长整型', value: 'bigint' },
        { label: '文本', value: 'text' }
      ],
      dataRule: {
        field: [
          { required: true, message: '列名不能为空', trigger: 'blur' },
          { pattern: /(^_([a-zA-Z0-9]_?)*$)|(^[a-zA-Z](_?[a-zA-Z0-9])*_?$)/, message: '请输入正确的列名', trigger: ['blur'] },
        ],
        fieldName: [
          { required: true, message: '说明不能为空', trigger: 'blur' },
          { validator: this.formValidate('fullName', '说明不能含有特殊符号'), trigger: 'blur' },
        ],
        dataType: [
          { required: true, message: '类型不能为空', trigger: 'change' }
        ],
        dataLength: [
          { required: true, message: '长度不能为空', trigger: 'change' }
        ],
        allowNull: [
          { required: true, message: '允许空不能为空', trigger: 'change' }
        ]
      }
    }
  },
  methods: {
    init(id) {
      this.visible = true
      this.dataForm.id = id || ''
      this.formLoading = true
      this.$nextTick(() => {
        this.$refs['dataForm'].resetFields()
        if (this.dataForm.id) {
          getInfo(this.dataForm.id).then(res => {
            this.dataForm = res.data
          })
        }
        this.formLoading = false
      })
    },
    dataFormSubmit() {
      this.$refs['dataForm'].validate((valid) => {
        if (valid) {
          this.btnLoading = true
          const formMethod = this.dataForm.id ? Update : Create
          formMethod(this.dataForm).then(res => {
            this.$message({
              message: res.msg,
              type: 'success',
              duration: 1500,
              onClose: () => {
                this.visible = false
                this.btnLoading = false
                this.$emit('refreshDataList')
              }
            })
          }).catch(() => {
            this.btnLoading = false
          })
        }
      })
    }
  }
}
