import { TableExampleInfo, TableExampleUpdate, TableExampleCreate } from '@/api/extend/tableExample'
export default {
  data() {
    return {
      visible: false,
      dataForm: {
        id: 0,
        projectName: '',
        projectCode: '',
        principal: '',
        jackStands: '',
        projectType: '',
        projectPhase: '',
        customerName: '',
        interactionDate: 0,
        costAmount: 0,
        tunesAmount: 0,
        projectedIncome: 0,
        description: '',
      },
      dataRule: {
        projectName: [
          { required: true, message: '项目名称不能为空', trigger: 'blur' }
        ],
        projectCode: [
          { required: true, message: '项目编码不能为空', trigger: 'blur' }
        ],
        principal: [
          { required: true, message: '负责人不能为空', trigger: 'blur' }
        ],
        projectType: [
          { required: true, message: '项目类型不能为空', trigger: 'change' }
        ],
      },
      industryTypeList: []
    }
  },
  methods: {
    init(industryTypeList, id) {
      this.industryTypeList = industryTypeList
      this.dataForm.id = id || 0
      this.visible = true
      this.$nextTick(() => {
        this.$refs['dataForm'].resetFields()
        if (this.dataForm.id) {
          TableExampleInfo(this.dataForm.id).then(res => {
            this.dataForm = res.data
          })
        }
      })
    },
    dataFormSubmit() {
      this.$refs['dataForm'].validate((valid) => {
        if (valid) {
          if (!this.dataForm.id) {
            delete (this.dataForm.id)
            TableExampleCreate(this.dataForm).then((res) => {
              this.$message({
                message: res.msg,
                type: 'success',
                duration: 1500,
                onClose: () => {
                  this.visible = false
                  this.$emit('refreshDataList')
                }
              })
            })
          } else {
            TableExampleUpdate(this.dataForm).then((res) => {
              this.$message({
                message: res.msg,
                type: 'success',
                duration: 1500,
                onClose: () => {
                  this.visible = false
                  this.$emit('refreshDataList')
                }
              })
            })
          }
        }
      })
    },
  }
}
