import Process from '@/components/Process/Preview'
import Form from './Form'
import {
  getVariateList,
  deleteVariate,
  copyVariate,
  exportVariate
} from '@/api/systemData/dataInterface'
export default {
  components: { Process, Form },
  props: [],
  data() {
    return {
      flowVisible: false,
      list: [],
      formVisible: false,
      listLoading: false,
      keyword: '',
      listQuery: {
        keyword: '',
        id: ''
      },
      id: '',
    }
  },
  methods: {
    init(id) {
      this.id = id
      this.getVariateList()
    },
    getVariateList() {
      this.listQuery.id = this.id
      getVariateList(this.listQuery).then(res => {
        this.list = res.data.list || []
        this.listLoading = false
      })
    },
    addOrUpdateHandle(item) {
      this.formVisible = true
      this.$nextTick(() => {
        this.$refs.Form.init(item, this.id)
      })
    },
    del(item) {
      this.$confirm('此操作将永久删除该数据，是否继续？', '系统提示', {
        type: 'warning'
      }).then(() => {
        deleteVariate(item.id).then(res => {
          if (res.code == 200) {
            this.$message({
              type: 'success',
              message: res.msg
            });
            this.getVariateList()
          }
        })
      })
    },
    goBack() {
      if (this.flowVisible) {
        return this.flowVisible = false
      }
      this.$emit('close', true)
    },
    reset() {
      this.keyword = ""
      this.listQuery.keyword = ""
      this.getVariateList()
    },
    search() {
      this.listQuery.keyword = this.keyword
      this.getVariateList()
    },
    handleCopy(id) {
      this.$confirm('您确定要复制该功能模板, 是否继续?', '提示', {
        type: 'warning'
      }).then(() => {
        copyVariate(id).then(res => {
          this.getVariateList()
        });
      })
    },
    handleExport(id) {
      this.$confirm('您确定要导出该变量, 是否继续?', '提示', {
        type: 'warning'
      }).then(() => {
        exportVariate(id).then(res => {
          this.jnpf.downloadFile(res.data.url)
        });
      })
    }
  }
}
