import {
  getCommonWordsInfo,
  Create, Update
} from '@/api/system/commonWords'
import { getSystem } from "@/api/system/system";
export default {
  data() {
    return {
      visible: false,
      formLoading: false,
      treeLoading: false,
      btnLoading: false,
      continueBtnLoading: false,
      singleSelectTreeVal: "", //单选树默认label值
      singleSelectTreeKey: "", //单选树默认key值
      systemOptions: [],
      value: '',
      dataForm: {
        commonWordsText: '',
        sortCode: '',
        commonWordsType: 0,
        enabledMark: 1,
      },
      nodeId: '0',
      dataRule: {
        commonWordsText: [
          { required: true, message: '请输入常用语', trigger: 'blur' },
        ],
      }
    }
  },
  methods: {
    init(id) {
      this.dataForm.id = id || 0
      this.visible = true
      this.formLoading = true
      this.$nextTick(() => {
        this.$refs['dataForm'].resetFields()
        this.treeLoading = true
        getSystem({ enableMark: "1" }).then(res => {
          this.systemOptions = res.data.list || []
        })
        if (this.dataForm.id) {
          getCommonWordsInfo(this.dataForm.id).then(res => {
            this.dataForm = res.data
          })
        }
        this.formLoading = false
      })
    },
    cancel() {
      this.visible = false
      this.$emit('refreshDataList')
    },
    dataFormSubmit(type) {
      this.$refs['dataForm'].validate((valid) => {
        if (valid) {
          if (type == 2) {
            this.continueBtnLoading = true
          } else {
            this.btnLoading = true
          }
          const formMethod = this.dataForm.id ? Update : Create
          formMethod(this.dataForm).then(res => {
            this.$message({
              message: res.msg,
              type: 'success',
              duration: 1500,
              onClose: () => {
                if (type == 2) {
                  this.$nextTick(() => {
                    this.$refs['dataForm'].resetFields()
                    this.continueBtnLoading = false
                  }
                  )
                } else {
                  this.visible = false
                  this.btnLoading = false
                  this.$emit('refreshDataList')
                }
              }
            })
          }).catch(() => {
            this.continueBtnLoading = false
            this.btnLoading = false
          })
        }
      })
    }
  }
}
