result:
.item {
  width: 100%;
  height: 100%;
  >>> .el-card {
    width: 100%;
    height: 100%;
    .el-card__body {
      padding: 0;
      position: relative;
    }
  }
}
.portal-box-body {
  width: 100%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 15px;

  .portal-box-item {
    width: 100%;
    height: 100%;
  }
}
.portal-eChart-tab-position {
  >>> .el-tabs__content {
    height: 100% !important;
  }
}
.portal-eChart-tab {
  width: 100%;
  height: 100%;
  background-color: #fff;
  overflow: hidden;
  >>> .is-top {
    margin: unset;
  }
  >>> .is-bottom {
    margin: unset !important;
  }
  >>> .el-tabs__content {
    height: calc(100% - 42px);
    display: flex;
    align-items: center;
    justify-content: center;
    overflow: visible;
    .el-tab-pane {
      width: 100%;
      height: 100%;
    }
    &:hover {
      .drawing-item-delete {
        display: block;
      }
    }
  }
}
.active-item {
  border: 1px solid #409eff;
}
.portal-box-item {
  &:hover {
    .drawing-item-delete {
      display: block;
    }
  }
}

.drawing-item-delete {
  display: none;
  position: absolute;
  top: 5px;
  right: 20px;
  width: 22px;
  height: 22px;
  line-height: 22px;
  text-align: center;
  border-radius: 50%;
  font-size: 12px;
  border: 1px solid;
  cursor: pointer;
  z-index: 20;
  border-color: #f56c6c;
  color: #f56c6c;
  background: #fff;
}

.el-tabs--border-card {
  >>> .el-tabs__content {
    padding: 0;
  }
}
