import SignImgDialog from '@/components/SignImgDialog'
import { mapGetters } from "vuex"
export default {
  components: { SignImgDialog },
  props: {
    assignNodeList: {
      type: Array,
      default: () => []
    }
  },
  data() {
    return {
      visible: false,
      properties: {},
      eventType: '',
      dataForm: {
        handleOpinion: '',
        freeApproverUserId: '',
        nodeCode: '',
        fileList: [],
      },
      signVisible: false,
      fromRules: {
        nodeCode: [
          { required: true, message: '请选择指派节点', trigger: 'change' }
        ]
      },
      signImg: '',
      btnLoading: false,
      title: '',
      label: ''
    }
  },
  computed: {
    ...mapGetters(['userInfo'])
  },
  methods: {
    init(properties, eventType) {
      this.visible = true
      this.properties = properties
      this.eventType = eventType || ''
      this.dataForm.handleOpinion = ''
      this.dataForm.freeApproverUserId = ''
      this.dataForm.nodeCode = ''
      this.dataForm.fileList = []
      this.signImg = ""
      if (this.properties.hasSign) this.signImg = this.userInfo.signImg
      switch (eventType) {
        case 'transfer':
          this.title = '转审'
          this.label = '转审'
          break;
        case 'revoke':
          this.title = '撤回流程'
          this.label = '撤回'
          break;
        case 'recall':
          this.title = '撤回审核'
          this.label = '撤回'
          break;
        case 'assign':
          this.title = '指派'
          this.label = '指派'
          break;
        case 'cancel':
          this.title = '终止审核不可恢复'
          this.label = '终止'
          break;
        case 'approvalCancel':
          this.title = '审批驳回终止流程'
          this.label = '驳回'
          break;
        default:
          break;
      }
      this.$nextTick(() => {
        this.$refs['dataForm'].resetFields()
      })
    },
    addSign() {
      this.signVisible = true
      this.$nextTick(() => {
        this.$refs.SignImg.init()
      })
    },
    signDialog(val) {
      this.signVisible = false
      if (val) {
        this.signImg = val
      }
    },
    handleSure() {
      this.$refs['dataForm'].validate((valid) => {
        if (valid) {
          if (this.properties.hasSign && !this.signImg && this.eventType != 'assign' && this.eventType != 'cancel') {
            this.$message({
              message: '请签名',
              type: 'error'
            })
            return
          }
          let query = {
            signImg: this.signImg,
            ...this.dataForm
          }
          this.btnLoading = true
          this.$emit('submit', query)
          this.closeDialog()
        }
      })
    },
    closeDialog() {
      this.btnLoading = false
      this.visible = false
    },
  }
}
