import { getPortalList, Delete, Copy, exportTemplate } from '@/api/onlineDev/portal'
import Form from './Form'
import PortalDesign from '@/components/VisualPortal/PortalDesign'
import previewDialog from '@/components/PreviewDialog'
import Preview from './IndexPreview'
import ReleaseDialog from './releaseDialog'
export default {
  name: 'onlineDev-visualPortal',
  components: { Form, PortalDesign, previewDialog, Preview, ReleaseDialog },
  data() {
    return {
      list: [],
      keyword: '',
      category: '',
      type: '',
      enabledLock: '',
      isRelease: '',
      listQuery: {
        currentPage: 1,
        pageSize: 20,
        sort: 'desc',
        sidx: ''
      },
      total: 0,
      currId: '',
      transferId: '',
      dialogVisible: false,
      previewVisible: false,
      previewTypeVisible: false,
      listLoading: false,
      formVisible: false,
      portalDesignVisible: false,
      categoryList: [],
      showAll: false,
      previewType: '',
      releaseDialog: false
    }
  },
  created() {
    this.getDictionaryData()
    this.initData()
  },
  methods: {
    reset() {
      this.keyword = ''
      this.category = ''
      this.type = ''
      this.enabledLock = ''
      this.isRelease = ''
      this.search()
    },
    search() {
      this.listQuery = {
        currentPage: 1,
        pageSize: 20,
        sort: 'desc',
        sidx: ''
      }
      this.initData()
    },
    getDictionaryData() {
      this.$store.dispatch('base/getDictionaryData', { sort: 'portalDesigner' }).then((res) => {
        this.categoryList = res
      })
    },
    initData() {
      this.listLoading = true
      let query = {
        ...this.listQuery,
        keyword: this.keyword,
        type: this.type,
        category: this.category,
        enabledLock: this.enabledLock,
        isRelease: this.isRelease
      }
      getPortalList(query).then(res => {
        this.list = res.data.list
        this.total = res.data.pagination.total
        this.listLoading = false
      })
    },
    handleDel(id) {
      this.$confirm(this.$t('common.delTip'), this.$t('common.tipTitle'), {
        type: 'warning'
      }).then(() => {
        Delete(id).then(res => {
          this.$message({
            type: 'success',
            message: res.msg,
            duration: 1000,
            onClose: () => {
              this.initData()
            }
          });
        })
      }).catch(() => { });
    },
    copy(id) {
      this.$confirm('您确定要复制该门户, 是否继续?', '提示', {
        type: 'warning'
      }).then(() => {
        Copy(id).then(res => {
          this.$message({
            type: 'success',
            message: res.msg,
            duration: 1000,
            onClose: () => {
              this.initData()
            }
          });
        })
      }).catch(() => { });
    },
    preview(id, type) {
      if (!id) return
      this.currId = id
      this.previewType = type
      this.$nextTick(() => {
        this.previewTypeVisible = true
      })
    },
    openReleaseDialog(row) {
      this.$nextTick(() => {
        this.releaseDialog = true
        this.$refs.release.openRelease(row)
      })
    },
    previewPc() {
      this.previewVisible = true
    },
    exportTemplate(id) {
      this.$confirm('您确定要导出该门户, 是否继续?', '提示', {
        type: 'warning'
      }).then(() => {
        exportTemplate(id).then(res => {
          this.jnpf.downloadFile(res.data.url)
        })
      }).catch(() => { });
    },
    design(row) {
      this.dialogVisible = false
      this.portalDesignVisible = true
      this.$nextTick(() => {
        this.$refs.portalDesign.init(row)
      })
    },
    addOrUpdateHandle(id) {
      this.dialogVisible = false
      this.formVisible = true
      this.$nextTick(() => {
        this.$refs.form.init(this.categoryList, id)
      })
    },
    closeForm(isRefresh) {
      this.formVisible = false
      if (isRefresh) this.initData()
    },
    closeForm1(isRefresh) {
      this.form1Visible = false
      if (isRefresh) this.initData()
    }
  }
}
