$lighterBlue: #409eff;
.JNPF-Portal {
  height: 100%;
  .el-aside,
  >>> .el-aside {
    padding: 0;
    margin: 0;
    border-radius: 4px;
    .cap-wrapper {
      position: relative;
      height: 42px;
      border-bottom: 1px solid #dcdfe6;
      box-sizing: border-box;
      line-height: 41px;
      padding-left: 10px;
      font-size: 16px;
    }
    .aside-scrollbar {
      height: calc(100% - 42px);
      overflow: hidden;

      .el-scrollbar__wrap {
        overflow-x: auto;
      }
    }
  }
  .left-box {
    background-color: #fff;
    border-radius: 4px;
    overflow: hidden;
  }
  .el-main {
    height: 100%;
    padding: 0;
    overflow: hidden;
    .action-bar {
      border-radius: 4px;
      position: relative;
      height: 40px;
      padding: 0 15px;
      box-sizing: border-box;
      background: #fff;
      margin-right: 10px;
      border: none;
      display: flex;
      border-bottom: 1px solid #dcdfe6;
      justify-content: space-between;
      .icon {
        color: #000 !important;
      }

      .delete-btn {
        color: #f56c6c !important;
      }

      .unActive-btn {
        color: #606266 !important;
        &:hover {
          color: #1890ff !important;
        }
      }

      .components-list {
        display: flex;
        align-items: center;

        .components-part {
          padding: 0 10px;
          margin-bottom: unset;

          .components-title {
            margin-bottom: unset;
            font-weight: normal;
            cursor: pointer;
          }
        }
      }
    }
    .empty-info {
      position: absolute;
      top: calc(50% - 200px);
      left: calc(50% - 200px);

      .empty-img {
        width: 400px;
        height: 400px;
      }
    }
    >>> .vue-grid-item.vue-grid-placeholder {
      background: $lighterBlue;
    }

    .layout-area {
      height: calc(100% - 36px);
      margin-left: -10px;
      margin-right: 10px;
      background: #fff;
      position: relative;
      border: none;
      #ipad {
        height: calc(100% - 42px);
        display: flex;
        justify-content: center;
        align-items: center;
        min-height: 781px;

        >>> .eChart-box-body {
          height: 250px !important;
        }

        .empty-info {
          top: calc(50% - 150px);
          left: calc(50% - 150px);

          .empty-img {
            width: 300px;
            height: 300px;
          }
        }

        .outeripad {
          background: url('../../../../assets/images/iphoneBg.png');
          width: 389px;
          height: 711px;
          padding: 65px 40px;

          .ipadHead {
            background: #f7f8f9;
            text-align: center;

            .ipadHead-img {
              margin: 0 auto;
              height: 20px;
            }
          }

          .ipadbody {
            height: 98%;
            // background: #ebeef5;
            .ipad-name {
              text-align: center;
              margin-bottom: 5px;
              margin-top: 5px;
            }
            .center-board-row > .el-form {
              height: 550px;
            }

            .center-scrollbar {
              height: 95.6672%;
              overflow: hidden;
            }
          }
        }
      }

      >>> .el-scrollbar__wrap {
        margin-bottom: 0 !important;
        overflow-x: auto;
      }
      .item-box {
        margin: 10px;
      }
      .drawing-board,
      .vue-grid-item,
      .item-box {
        position: relative;
        height: 100%;
        border: 1px dashed #e2e0e0;

        >>> .vue-resizable-handle {
          z-index: 20;
        }
        &.active-item {
          border: 1px solid $lighterBlue;
          & > .drawing-item-copy,
          & > .drawing-item-delete {
            display: block !important;
          }
        }
        .mask {
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          z-index: 9;
        }
        .drawing-item-copy,
        .drawing-item-delete {
          display: none;
          position: absolute;
          top: -10px;
          width: 22px;
          height: 22px;
          line-height: 22px;
          text-align: center;
          border-radius: 50%;
          font-size: 12px;
          border: 1px solid;
          cursor: pointer;
          z-index: 20;
        }

        .drawing-item-copy {
          right: 56px;
          border-color: $lighterBlue;
          color: $lighterBlue;
          background: #fff;

          &:hover {
            background: $lighterBlue;
            color: #fff;
          }
        }

        .drawing-item-delete {
          right: 20px;
          border-color: #f56c6c;
          color: #f56c6c;
          background: #fff;

          &:hover {
            background: #f56c6c;
            color: #fff;
          }
        }
        &:hover {
          .drawing-item-copy,
          .drawing-item-delete {
            display: block;
          }
        }
        >>> .el-card {
          width: 100%;
          height: 100%;
          .el-card__body {
            padding: 0;
          }
        }
      }
    }
  }
}
.components-box {
  >>> .components-item {
    line-height: 35px;
    padding: 0 25px 0 20px;
  }
}
.ml-10 {
  >>> .el-scrollbar__wrap {
    margin-left: 10px !important;
  }
}
