export default {
  name: 'JNPF-transfer',
  data() {
    return {
      selectedData: [],
      selectedTextData: [],
      filterText: '',
      activeName: 'department',
    }
  },
  props: {
    height: {
      type: String,
      default: "400px"
    },
    loading: {
      type: Boolean,
      default: false
    },
    dynamicList: {
      type: Array,
      default: () => []
    },
    multiple: {
      type: Boolean,
      default: false
    },
    treeData: {
      type: Array,
      default: () => []
    },
    value: {
      type: Array,
      default: () => []
    },
    props: {
      type: Object,
      default: () => {
        return { value: "id", label: "fullName", children: "children" };
      }
    },
    type: {
      type: String,
      default: ''
    }
  },
  computed: {
    allList() {
      if (!this.type) return []
      let allList = this.$store.getters[this.type + 'List'] || []
      return [...allList, ...this.dynamicList]
    }
  },
  watch: {
    value(val) {
      this.initHandle()
    },
  },
  methods: {
    initHandle() {
      if (!this.value || !this.value.length) {
        this.selectedTextData = []
        this.selectedData = []
        return
      }
      const arr = this.multiple ? this.value : [this.value]
      let selectedTextData = []
      for (let i = 0; i < arr.length; i++) {
        const item = arr[i];
        inner: for (let j = 0; j < this.allList.length; j++) {
          if (item === this.allList[j].id) {
            selectedTextData.push(this.allList[j])
            break inner
          }
        }
      }
      this.selectedTextData = selectedTextData
      this.selectedData = this.selectedTextData.map(o => o.id)
    },
    search() {
      this.$refs.treeBox.filter(this.filterText);
    },
    filterNode(value, data) {
      if (!value) return true;
      return data.fullName.indexOf(value) !== -1;
    },
    handleNodeClick(data) {
      if (this.activeName === 'department' && data.type !== this.type) return
      const boo = this.selectedTextData.some(o => o.id === data.id)
      if (boo) return
      this.multiple ? this.selectedTextData.push(data) : this.selectedTextData = [data]
      this.selectedData = this.selectedTextData.map(o => o.id)
      this.$emit('input', this.selectedData)
      this.$emit('getValue', this.selectedData, this.selectedTextData)
    },
    removeData(item, index) {
      this.selectedData.splice(index, 1)
      this.selectedTextData.splice(index, 1)
      this.$emit('input', this.selectedData)
      this.$emit('getValue', this.selectedData, this.selectedTextData)
    },
    removeAll() {
      this.selectedData = []
      this.selectedTextData = []
      this.$emit('input', this.selectedData)
      this.$emit('getValue', this.selectedData, this.selectedTextData)
    },
  }
};
