import { getAmountChinese, thousandsFormat } from "@/components/Generator/utils/index"
export default {
  name: 'JNPF-number',
  components: {},
  props: {
    value: {
      type: [Number, String],
      default: undefined
    },
    thousands: {
      type: Boolean,
      default: false
    },
    isAmountChinese: {
      type: Boolean,
      default: false
    },
    addonBefore: {
      type: String,
      default: ''
    },
    addonAfter: {
      type: String,
      default: ''
    },
    precision: {
      type: Number,
      default: undefined
    },
  },
  data() {
    return {
      innerValue: 0,
      amountChineseName: ''
    }
  },
  watch: {
    value: {
      handler(val) {
        this.innerValue = val
        this.thousandSeparator()
      },
      immediate: true
    }
  },
  computed: {},
  created() { },
  mounted() {
  },
  methods: {
    thousandSeparator() {
      if (this.isAmountChinese) this.amountChineseName = getAmountChinese(this.innerValue)
      if (this.precision) this.innerValue = Number(this.innerValue).toFixed(this.precision)
      if (this.thousands) {
        this.innerValue = thousandsFormat(this.innerValue)
      }
      return this.innerValue
    },
  }
}
