import * as echarts from 'echarts';
import resize from '../mixins/resize'
export default {
  name: 'extend-graphDemo-echartsBar',
  mixins: [resize],
  data() {
    return {
      chart: null,
      option: {
        title: {
          text: '堆叠分类',
        },
        tooltip: {
          trigger: 'axis',
