.todo-box-body {
  display: flex;
  flex-wrap: wrap;
  .item-box {
    box-shadow: 1px 0 #f0f0f0, 0 1px #f0f0f0, 1px 1px #f0f0f0, 1px 0 #f0f0f0 inset, 0 1px #f0f0f0 inset;
    transition: all 0.3s;
    &:hover {
      position: relative;
      z-index: 1;
      box-shadow: 0 1px 2px -2px #00000029, 0 3px 6px #0000001f, 0 5px 12px 4px #00000017;
    }
  }
  .item {
    height: 141px;
    display: flex;
    align-items: center;
    overflow: hidden;
    i {
      width: 56px;
      height: 56px;
      margin-right: 14px;
      border-radius: 50%;
      color: #fff;
      display: inline-block;
      vertical-align: top;
      text-align: center;
      line-height: 56px;
      font-size: 30px;
      margin-left: 30px;
    }
    .text {
      display: inline-block;
      height: 56px;
      .num {
        font-size: 20px;
        line-height: 36px;
      }
      .name {
        font-size: 14px;
        color: #666;
      }
    }
  }
}
