import { getDataBackupList, createDataBackup, delDataBackup } from '@/api/systemData/dataBackup'

export default {
  name: 'systemData-dataBackup',
  data() {
    return {
      listQuery: {
        keyword: '',
        currentPage: 1,
        pageSize: 20
      },
      tableList: [],
      total: 0,
      btnLoading: false,
      listLoading: true
    }
  },
  created() {
    this.initData()
  },
  methods: {
    initData() {
      this.listLoading = true
      getDataBackupList(this.listQuery).then(res => {
        this.tableList = res.data.list
        this.total = res.data.pagination.total
        this.listLoading = false
        this.btnLoading = false
      }).catch(() => {
        this.listLoading = false
        this.btnLoading = false
      })
    },
    handleCrateBackup() {
      this.$confirm('您确定要备份数据库吗, 是否继续？', '提示', {
        type: 'warning'
      }).then(() => {
        createDataBackup().then(res => {
          this.$message({
            type: 'success',
            message: res.msg,
            duration: 1500,
            onClose: () => {
              this.initData()
            }
          })
        })
      }).catch(() => { })
    },
    handleDel(id) {
      this.$confirm('此操作将永久删除该文件, 是否继续?', '提示', {
        type: 'warning'
      }).then(() => {
        delDataBackup(id).then(res => {
          this.$message({
            type: 'success',
            message: res.msg,
            duration: 1500,
            onClose: () => {
              this.initData()
            }
          })
        })
      }).catch(() => { })
    },
    handleDownload(fileUrl) {
      this.jnpf.downloadFile(fileUrl)
    },
    search() {
      this.listQuery.currentPage = 1
      this.listQuery.pageSize = 20
      this.listQuery.sort = 'desc'
      this.initData()
    },
    reset() {
      this.listQuery.keyword = ''
      this.search()
    }
  }
}
