import {
  getButtonAuthorizeSelector,
  createButton,
  updateButton,
  getButtonInfo
} from '@/api/system/buttonAuthorize'
import iconBox from '@/components/JNPF-iconBox'

export default {
  components: { iconBox },
  data() {
    return {
      visible: false,
      formLoading: false,
      btnLoading: false,
      treeData: [],
      moduleId: '',
      dataForm: {
        id: '',
        parentId: '',
        fullName: '',
        enCode: '',
        sortCode: 0,
        icon: '',
        enabledMark: 1,
        description: ''
      },
      iconBoxVisible: false,
      dataRule: {
        parentId: [
          { required: true, message: '上级不能为空', trigger: 'input' }
        ],
        fullName: [
          { required: true, message: '按钮名称不能为空', trigger: 'blur' }
        ],
        enCode: [
          { required: true, message: '按钮编码不能为空', trigger: 'blur' }
        ]
      }
    }
  },
  methods: {
    init(moduleId, id) {
      this.dataForm.id = id || ''
      this.dataForm.moduleId = moduleId
      this.visible = true
      this.formLoading = true
      this.$nextTick(() => {
        this.$refs['dataForm'].resetFields()
        // 获取上级按钮
        getButtonAuthorizeSelector(this.dataForm.moduleId).then(res => {
          let topItem = {
            fullName: "顶级节点",
            hasChildren: true,
            id: "-1",
            children: res.data.list
          }
          this.treeData = [topItem]
        })

        // 获取按钮数据
        if (this.dataForm.id) {
          getButtonInfo(this.dataForm.id).then(res => {
            this.dataForm = res.data
          })
        }
        this.formLoading = false
      })
    },
    dataFormSubmit() {
      this.$refs['dataForm'].validate((valid) => {
        if (valid) {
          this.btnLoading = true
          const formMethod = this.dataForm.id ? updateButton : createButton
          formMethod(this.dataForm).then(res => {
            this.$message({
              message: res.msg,
              type: 'success',
              duration: 1500,
              onClose: () => {
                this.visible = false
                this.btnLoading = false
                this.$emit('refreshDataList')
              }
            })
          }).catch(() => {
            this.btnLoading = false
          })
        }
      })
    },
    openIconBox() {
      this.iconBoxVisible = true
    },
    choiceIcon(value) {
      this.dataForm.icon = value
    }
  }
}
