.comment-container {
  height: 100%;
  overflow: hidden;
  .comment-list {
    height: 100%;
    overflow: auto;
    .item {
      padding: 0 30px;
      margin-bottom: 20px;
      .head {
        display: flex;
        align-items: center;
        .avatar {
          flex-shrink: 0;
          margin-right: 20px;
        }
        .username {
          line-height: 40px;
          color: #333;
          flex: 1;
        }
        .head-right {
          flex-shrink: 0;
        }
        .time {
          flex-shrink: 0;
          font-size: 14px;
          color: #666;
          margin-left: 20px;
        }
      }

      .content {
        font-size: 14px;
        padding-left: 60px;
        .text {
          line-height: 30px;
          color: #666;
        }
        .img-list {
          margin-top: 6px;
          .img-item {
            width: 80px;
            height: 80px;
            overflow: hidden;
            margin: 0 6px 6px 0;
            // border: 1px solid #c0ccda;
            border-radius: 6px;
          }
        }
      }
    }
  }
}
.JNPF-dialog {
  .upload-btn {
    display: inline-block;
    margin-right: 20px;
  }
  .img-list {
    display: flex;

    .img-item {
      width: 40px;
      height: 40px;
      position: relative;
      margin-right: 10px;
      .el-image {
        width: 100%;
        height: 100%;
      }
      .badge {
        background-color: #f56c6c;
        border-radius: 10px;
        color: #fff;
        display: block;
        font-size: 12px;
        height: 18px;
        width: 18px;
        line-height: 18px;
        text-align: center;
        border: 1px solid #fff;
        position: absolute;
        right: -9px;
        top: -9px;
        cursor: pointer;
        z-index: 10001;
      }
    }
  }
  .el-upload-list__item {
    .el-upload-list__item-name {
      margin-right: 70px;
      .el-icon-paperclip {
        line-height: 25px;
      }
    }
    .el-icon-download {
      display: inline-block;
      position: absolute;
      top: 5px;
      right: 25px;
      cursor: pointer;
      opacity: 0.75;
      color: #606266;
    }
    .el-icon-view {
      display: inline-block;
      position: absolute;
      top: 5px;
      right: 45px;
      cursor: pointer;
      opacity: 0.75;
      color: #606266;
    }
  }
}
