import Sortable from 'sortablejs'
export default {
  data() {
    return {
      visible: false,
      options: [],
      list: [],
      data: {},
      sortTypeOptions: [
        { id: 'asc', fullName: '升序' },
        { id: 'desc', fullName: '降序' },
      ],
    }
  },
  methods: {
    init(option) {
      this.data = option ? JSON.parse(JSON.stringify(option)) : {}
      this.list = this.data.list
      this.visible = true
      this.$nextTick(() => {
        this.setSort()
      });
    },
    setSort() {
      const el = this.$refs.dragTable.$el.querySelectorAll('.el-table__body-wrapper > table > tbody')[0]
      this.sortable = Sortable.create(el, {
        ghostClass: 'sortable-ghost', // Class name for the drop placeholder,
        setData: function (dataTransfer) {
          dataTransfer.setData('Text', '')
        },
        onEnd: evt => {
          const targetRow = this.list.splice(evt.oldIndex, 1)[0]
          this.list.splice(evt.newIndex, 0, targetRow)
        },
        handle: '.drag-handler',
      })
    },
    getOptions(index) {
      let options = this.data.columnOptions.filter(o => !['editor'].includes(o.__config__.jnpfKey));;
      for (let i = 0; i < this.list.length; i++) {
        const e = this.list[i];
        if (e.field && index !== i) options = options.filter(o => o.id !== e.field);
      }
      return options;
    },
    closeDialog() {
      let isOk = true;
      for (let i = 0; i < this.list.length; i++) {
        if (!this.list[i].field) {
          this.$message({ message: '字段不能为空', type: 'warning' })
          isOk = false;
          break;
        }
      }
      if (!isOk) return;
      this.$emit('confirm', this.list)
      this.visible = false
    },
    handleDel(index) {
      this.list.splice(index, 1);
    },
    addHandle() {
      let id = 'sort' + this.jnpf.idGenerator()
      let item = { field: "", sort: 'desc', id: id }
      this.list.push(item)
    }
  }
}
