import printOptionApi from "./printMinxin.js";
export default {
  mixins: [printOptionApi],
  data() {
    return {
      pageIndex: 1,
    };
  },
  watch: {
    pageIndex: {
      handler(val) {
        this.scrollpage(val);
      },
      deep: true,
      immediate: true
    },
  },
  methods: {
    scrollpage() {
      let index = this.pageIndex - 1 < 0 ? 0 : this.pageIndex - 1;
      this.$nextTick(() => {
        let dom = this.$refs.tsPrint
        if (dom) {
          dom.scrollTo({
            top: (window.document.body.clientHeight - 10) * index,
            behavior: "smooth",
          });
        }
      });
    },
    onOpen() {
      this.initData('batch')
    },
  },
};
