import {
  getOnlineUser,
  batchDelOnlineUser,
  deleteOnlineUser
} from '@/api/permission/onlineUser'

export default {
  name: 'permission-userOnline',
  data() {
    return {
      formVisible: false,
      tableDataList: [],
      refreshLoading: false,
      listLoading: true,
      multipleSelection: [],
      listQuery: {
        keyword: '',
        currentPage: 1,
        pageSize: 20,
        sort: 'desc',
        sidx: ''
      },
      total: 0,
    }
  },
  created() {
    this.initData()
  },
  methods: {
    reset() {
      this.listQuery.keyword = ''
      this.initData()
    },
    search() {
      this.listQuery = {
        ...this.listQuery,
        currentPage: 1,
        pageSize: 20,
        sort: 'desc',
        sidx: ''
      },
        this.initData()
    },
    initData() {
      this.listLoading = true
      getOnlineUser(this.listQuery).then(res => {
        this.tableDataList = res.data.list || []
        this.total = res.data.pagination.total
        this.listLoading = false
        this.refreshLoading = false
      }).catch(() => {
        this.listLoading = false
        this.refreshLoading = false
      })
    },
    handleSelectionChange(val) {
      const res = val.map(item => item.userId)
      this.multipleSelection = res
    },
    batchDel() {
      if (!this.multipleSelection.length) {
        this.$message({
          type: 'error',
          message: '请选择一条数据',
          duration: 1500,
        })
        return
      }
      this.$confirm('您确定要强制下线这些用户吗, 是否继续?', '提示', {
        type: 'warning'
      }).then(() => {
        batchDelOnlineUser(this.multipleSelection).then(res => {
          this.$message({
            type: 'success',
            message: res.msg,
            duration: 1500,
            onClose: () => {
              this.initData()
            }
          })
        })
      }).catch(() => { })
    },
    handleDel(userId) {
      this.$confirm(this.$t(`userOnline.cancelAccountTip`), this.$t(`common.tipTitle`), {
        type: 'warning'
      }).then(() => {
        deleteOnlineUser(userId).then(res => {
          this.$message({
            type: 'success',
            message: res.msg,
            duration: 1500,
            onClose: () => {
              this.initData()
            }
          })
        })
      }).catch(() => { })
    }
  }
}
