import { ExportExcel } from '@/api/extend/employee'
export default {
  data() {
    return {
      visible: false,
      btnLoading: false,
      type: 0,
      columns: [],
      columnList: [
        {
          label: "工号",
          prop: "enCode"
        },
        {
          label: "姓名",
          prop: "fullName"
        },
        {
          label: "性别",
          prop: "gender"
        },
        {
          label: "部门",
          prop: "departmentName"
        },
        {
          label: "岗位",
          prop: "positionName"
        },
        {
          label: "用工性质",
          prop: "workingNature"
        },
        {
          label: "身份证号",
          prop: "idNumber"
        },
        {
          label: "联系电话",
          prop: "telephone"
        },
        {
          label: "出生年月",
          prop: "birthday"
        },
        {
          label: "参加工作",
          prop: "attendWorkTime"
        },
        {
          label: "最高学历",
          prop: "education"
        },
        {
          label: "所学专业",
          prop: "major"
        },
        {
          label: "毕业院校",
          prop: "graduationAcademy"
        },
        {
          label: "毕业时间",
          prop: "graduationTime"
        },
        {
          label: "创建时间",
          prop: "creatorTime"
        },
      ],
      listQuery: {},
      checkAll: true,
      isIndeterminate: false
    }
  },
  methods: {
    init(listQuery) {
      this.visible = true
      this.checkAll = true
      this.isIndeterminate = false
      this.btnLoading = false
      this.listQuery = listQuery
      this.columns = this.columnList.map(o => o.prop)
    },
    handleCheckAllChange(val) {
      this.columns = val ? this.columnList.map(o => o.prop) : [];
      this.isIndeterminate = false;
    },
    handleCheckedChange(value) {
      let checkedCount = value.length;
      this.checkAll = checkedCount === this.columnList.length;
      this.isIndeterminate = checkedCount > 0 && checkedCount < this.columnList.length;
    },
    downLoad() {
      if (!this.columns.length) return this.$message.warning(`请至少选择一个导出字段`)
      this.btnLoading = true
      let query = {
        ...this.listQuery,
        dataType: this.type,
        selectKey: this.columns.join(',')
      }
      ExportExcel(query).then(res => {
        this.btnLoading = false
        if (!res.data.url) return
        this.jnpf.downloadFile(res.data.url)
        this.visible = false
      }).catch(() => { this.btnLoading = false })
    }
  }
}
