import {
  getBillRuleList,
  delBillRule,
  updateBillRuleState,
  exportTpl
} from '@/api/system/billRule'
import Form from './Form'

export default {
  name: 'system-billRule',
  components: {
    Form
  },
  data() {
    return {
      tableList: [],
      total: 0,
      btnLoading: false,
      listLoading: true,
      categoryId: '',
      enabledMark: '',
      listQuery: {
        keyword: '',
        currentPage: 1,
        pageSize: 20
      },
      formVisible: false,
      categoryList: []
    }
  },
  created() {
    this.$store.dispatch('base/getDictionaryData', { sort: 'businessType' }).then((res) => {
      this.categoryList = res
      this.initData()
    })
  },
  methods: {
    initData() {
      this.listLoading = true
      let query = {
        ...this.listQuery,
        categoryId: this.categoryId,
        enabledMark: this.enabledMark
      }
      getBillRuleList(query).then(res => {
        this.tableList = res.data.list
        this.total = res.data.pagination.total
        this.listLoading = false
        this.btnLoading = false
      }).catch(() => {
        this.listLoading = false
        this.btnLoading = false
      })
    },
    handleUpdateState(row) {
      const txt = row.enabledMark ? '禁用' : '开启'
      this.$confirm(`您确定要${txt}当前单据吗, 是否继续?`, '提示', {
        type: 'warning'
      }).then(() => {
        updateBillRuleState(row.id).then(res => {
          this.$message({
            type: 'success',
            message: res.msg,
            duration: 1000,
            onClose: () => {
              row.enabledMark = row.enabledMark ? 0 : 1
            }
          })
        })
      }).catch(() => { })
    },
    addOrUpdateHandle(id) {
      this.formVisible = true
      this.$nextTick(() => {
        this.$refs.Form.init(id, this.categoryList)
      })
    },
    handleDel(id) {
      this.$confirm(this.$t('common.delTip'), this.$t('common.tipTitle'), {
        type: 'warning'
      }).then(() => {
        delBillRule(id).then(res => {
          this.$message({
            type: 'success',
            message: res.msg,
            duration: 1500,
            onClose: () => {
              this.initData()
            }
          })
        })
      }).catch(() => { })
    },
    exportTpl(id) {
      this.$confirm('您确定要导出该单据, 是否继续?', '提示', {
        type: 'warning'
      }).then(() => {
        exportTpl(id).then(res => {
          this.jnpf.downloadFile(res.data.url)
        })
      }).catch(() => { });
    },
    search() {
      this.listQuery.currentPage = 1
      this.listQuery.pageSize = 20
      this.listQuery.sort = 'desc'
      this.initData()
    },
    reset() {
      this.listQuery.keyword = ''
      this.categoryId = ''
      this.enabledMark = ''
      this.search()
    }
  }
}
