.recordSummary {
  .el-tab-pane {
    height: 0;
  }
  >>> .el-tabs__content {
    height: 100%;
    .recordSummary-list {
      height: 100%;
      overflow: hidden auto;
      padding: 50px 100px 0;
      .recordSummary-item {
        margin-bottom: 20px;
        .el-card__body {
          padding: 0;
          .recordSummary-item-main {
            display: flex;
            align-items: center;
            .cap {
              width: 150px;
              flex-shrink: 0;
              text-align: center;
              font-size: 14px;
            }
            .content {
              flex: 1;
              padding: 0px 36px;
              border-left: 1px solid #e4e7ed;
            }
            .child-item {
              padding: 20px 0;
              font-size: 14px;
              line-height: 22px;
              &:last-child {
                margin-bottom: 0;
              }
              &:nth-child(2n) {
                border-top: 1px solid #e1e5eb;
              }
              .child-item-block {
                display: flex;
                align-items: flex-start;
                flex-direction: row;
                .avatar {
                  width: 40px;
                  height: 40px;
                }
                // padding: 10px;

                .child-item-title {
                  flex: 1;
                  margin-bottom: 2px;
                  display: flex;
                  justify-content: space-between;
                  flex-direction: column;
                  margin-left: 5px;
                  .child-item-line {
                    display: flex;
                    align-items: center;
                    flex-direction: row;
                    justify-content: space-between;
                    padding-left: 4px;
                    .name {
                      font-weight: 600;
                      span {
                        font-weight: 400;
                        font-size: 12px;
                      }
                    }
                    .tag {
                      float: right;
                    }
                  }
                  .child-item-option {
                    color: #747579;
                    padding-left: 4px;
                  }
                  .status {
                    flex-shrink: 0;
                    .el-link {
                      cursor: auto !important;
                    }
                  }
                }
              }
            }
          }
        }
      }
    }
  }
}
