import JNPFCodeEditor from '@/components/JNPFEditor/monaco'
import ScriptDemo from '@/components/Generator/index/ScriptDemo';
export default {
  components: { JNPFCodeEditor, ScriptDemo },
  props: ['value', 'type'],
  data() {
    return {
      text: '',
      options: {
        language: 'javascript'
      },
      title: "",
      dialogVisible: false
    }
  },

  methods: {
    init() {
      this.dialogVisible = true
      this.text = this.value
      this.$nextTick(() => {
        this.$refs.CodeEditor.changeEditor({
          value: this.text,
          options: this.options
        })
      });
      this.setTitle()
    },
    onClose() {
      this.$emit('updateScript', this.text)
      this.closeDialog()
    },
    closeDialog() {
      this.$emit('closeDialog')
    },
    setTitle() {
      switch (this.type) {
        case 'afterOnload':
          this.title = "表格脚本"
          break;
        case 'rowStyle':
          this.title = "行样式"
          break;
        case 'cellStyle':
          this.title = "单元格样式"
          break;
        default:
          break;
      }
    }
  }
}
