import { BigDataList, BigDataCreate } from '@/api/extend/bigData'
export default {
  name: 'extend-bigData',
  data() {
    return {
      keyword: '',
      list: [],
      total: 0,
      listLoading: true,
      btnLoading: false,
      listQuery: {
        currentPage: 1,
        pageSize: 20,
        sort: 'desc'
      },
    }
  },
  created() {
    this.initData()
  },
  methods: {
    search() {
      this.listQuery = {
        currentPage: 1,
        pageSize: 20,
        sort: 'desc'
      }
      this.initData()
    },
    reset() {
      this.keyword = ''
      this.search()
    },
    initData() {
      this.listLoading = true
      let query = {
        ...this.listQuery,
        keyword: this.keyword
      }
      BigDataList(query).then(res => {
        this.list = res.data.list
        this.total = res.data.pagination.total
        this.listLoading = false
      })
    },
    // 新增 
    addHandle() {
      this.$confirm('您确定要创建10000条数据吗, 是否继续?', '提示', {
        type: 'warning'
      }).then(() => {
        this.btnLoading = true
        BigDataCreate().then(res => {
          this.$message({
            message: res.msg,
            type: 'success',
            duration: 1500,
          })
          this.btnLoading = false
          this.initData()
        })
      }).catch(() => { this.btnLoading = false })
    }
  }
}
