.noticeList {
  >>> .el-table tr:last-child td {
    border-bottom: unset;
  }
}
.portal-table-box-body {
  height: 100%;
  overflow: auto;
}
.portal-notice-box {
  display: flex;
  flex-direction: column;

  >>> .el-card__header {
    height: 55px;
    padding: 0;
  }
  >>> .el-card__body {
    width: 100%;
    flex: 1;
    overflow: hidden;
  }
  .portal-notice-box-body {
    height: 100%;
    overflow: auto;
    .scope-a:hover {
      color: #189eff;
    }
    .portal-list-box {
      display: flex;
      flex-direction: column;
      .portal-list-item {
        display: flex;
        height: 100%;
        width: 100%;
        padding: 12px 0;
        align-items: center;
        .item-image-box {
          width: 52px;
          height: 52px;
          flex-shrink: 0;
          .item-image {
            width: 100%;
            height: 100%;
            border-radius: 50%;
          }
        }
        .item-r {
          flex: 1;
          display: flex;
          flex-direction: column;
          justify-content: space-between;
          margin-left: 10px;
          overflow: hidden;
          white-space: nowrap;
          text-overflow: ellipsis;
          .item-r-inner {
            display: flex;
            margin-bottom: 5px;
            align-items: center;
            .item-r-inner-type {
              padding-right: 10px;
            }
            .item-r-title {
              overflow: hidden;
              flex: 1;
              white-space: nowrap;
              text-overflow: ellipsis;
              padding-right: 10px;
            }
            .item-r-title:hover {
              color: #189eff !important;
            }
          }
          .item-r-content {
            overflow: hidden;
            white-space: nowrap;
            text-overflow: ellipsis;
            margin-top: 5px;
          }
          .item-r-time {
            margin-top: 5px;
          }
        }
      }
    }
  }
}
