import { CacheManageList, CacheManageClear, CacheManageClearAll } from '@/api/system/cacheManage'
import Form from './Form'
export default {
  name: 'system-cache',
  components: { Form },
  data() {
    return {
      keyword: '',
      overdueTime: [],
      list: [],
      listLoading: true,
      formVisible: false
    }
  },
  created() {
    this.initData()
  },
  methods: {
    search() {
      this.initData()
    },
    reset() {
      this.keyword = ''
      this.overdueTime = []
      this.search()
    },
    initData() {
      this.listLoading = true
      let listQuery = {
        keyword: this.keyword,
        overdueStartTime: this.overdueTime[0],
        overdueEndTime: this.overdueTime[1]
      }
      CacheManageList(listQuery).then(res => {
        this.list = res.data.list
        this.listLoading = false
      })
    },
    handleDel(index, id) {
      this.$confirm('您确定要清空缓存数据吗, 是否继续?', '提示', {
        type: 'warning'
      }).then(() => {
        this.asyncDel(index, id);
      }).catch(() => { });
    },
    asyncDel(index, id) {
      CacheManageClear(id).then(res => {
        this.list.splice(index, 1);
        this.$message({
          type: 'success',
          message: res.msg
        });
        // this.initData()
      })
    },
    ClearAll() {
      this.$confirm('您确定要清空所有缓存数据吗, 是否继续?', '提示', {
        type: 'warning'
      }).then(() => {
        CacheManageClearAll().then(res => {
          this.$message({
            type: 'success',
            message: res.msg
          });
          this.reset()
        })
      }).catch(() => { });
    },
    // 新增 / 修改
    addOrUpdateHandle(id) {
      this.formVisible = true
      this.$nextTick(() => {
        this.$refs.Form.init(id)
      })
    }
  }
}
