.tags-view-container {
  height: 39px;
  width: 100%;
  background-color: #fff;
  border-bottom: 1px solid #e8eaec;
  .el-tabs__btn {
    display: inline-block;
    width: 40px;
    height: 38px;
    line-height: 38px;
    text-align: center;
    cursor: pointer;
    background-color: #fff;
    z-index: 10;
    position: absolute;
    &:first-of-type {
      border-right: 1px solid #e8eaec;
    }
    &:last-of-type {
      border-left: 1px solid #e8eaec;
    }
    >>> .icon-ym {
      font-size: 16px;
    }
  }
  .tags-view-wrapper {
    box-sizing: border-box;
    .tags-view-item {
      display: inline-block;
      position: relative;
      cursor: pointer;
      height: 38px;
      line-height: 38px;
      border-right: 1px solid #e8eaec;
      color: #606266;
      background-color: #fff;
      padding: 0 18px;
      font-size: 14px;
      .tag-icon {
        vertical-align: middle;
        font-size: 16px;
      }
      .tag-icon + span {
        line-height: 38px;
        vertical-align: middle;
        margin-left: 4px;
      }
      &:first-of-type {
        margin-left: 40px;
      }
      &:last-of-type {
        margin-right: 40px;
      }
      &:hover {
        color: #409eff;
      }
      &.active {
        background-color: #ebeef5;
        color: #409efe;
        height: 40px;
        &::after {
          position: absolute;
          left: 0;
          bottom: -1px;
          height: 1px;
          width: 100%;
          display: block;
          content: '';
          background: #ebeef5;
          z-index: 100;
        }
      }
      .el-icon-close {
        border-radius: 50%;
        text-align: center;
        position: relative;
        cursor: pointer;
        font-size: 12px;
        height: 16px;
        width: 16px;
        line-height: 16px;
        vertical-align: middle;
        top: 0px;
        right: -2px;
      }
    }
  }
  .contextmenu {
    margin: 0;
    background: #fff;
    z-index: 3000;
    position: absolute;
    list-style-type: none;
    padding: 5px 0;
    border-radius: 4px;
    font-size: 12px;
    font-weight: 400;
    color: #333;
    box-shadow: 2px 2px 3px 0 rgba(0, 0, 0, 0.3);
    li {
      margin: 0;
      padding: 7px 16px;
      cursor: pointer;
      &:hover {
        background: #eee;
      }
    }
  }
}
