export default {
  name: 'JnpfSelect',
  components: {},
  props: {
    value: {
      default: undefined
    },
    options: {
      type: Array,
      default: () => []
    },
    placeholder: {
      type: String,
      default: '请选择'
    },
    disabled: {
      type: Boolean,
      default: false
    },
    multiple: {
      type: Boolean,
      default: false
    },
    filterable: {
      type: Boolean,
      default: false
    },
    clearable: {
      type: Boolean,
      default: true
    },
    props: {
      label: "fullName",
      value: "id"
    }
  },
  data() {
    return {
      innerValue: this.value,
    }
  },
  watch: {
    value: {
      handler(val) {
        this.innerValue = val
      },
      immediate: true
    },
  },
  computed: {},
  mounted() { },
  methods: {
    onChange() {
      if (this.multiple) this.innerValue = this.innerValue ? this.innerValue : []
      if (!this.multiple) this.innerValue = this.innerValue ? this.innerValue : this.innerValue === 0 ? 0 : ""
      this.$emit('input', this.innerValue)
      this.$emit('change', this.innerValue)
    },
  }

}
