import Form from "./Form";
import MenuManage from "./menuManage";
import PortalManagement from './portalManagement'
import { getSystem, delSystem } from "@/api/system/system";
export default {
  name: "system-menu",
  components: {
    Form,
    MenuManage,
    PortalManagement
  },
  data() {
    return {
      options: [],
      listQuery: {
        keyword: "",
        enabledMark: ''
      },
      list: [],
      btnLoading: false,
      listLoading: false,
      formVisible: false,
      expands: true,
      refreshTable: true,
      previewVisible: false,
      total: 0,
      portalVisible: false
    };
  },
  created() {
    this.initData();
  },
  methods: {
    portalManagement(row) {
      this.portalVisible = true
      this.$nextTick(() => {
        this.$refs.portalManage.init(row);
      })
    },
    closeForm(isRefresh) {
      this.previewVisible = false
      this.portalVisible = false
      if (isRefresh) this.refresh()
    },
    reset() {
      this.listQuery.keyword = "";
      this.listQuery.enabledMark = ""
      this.initData();
    },
    initData() {
      this.listLoading = true;
      getSystem(this.listQuery).then((res) => {
        this.list = res.data.list;
        this.listLoading = false;
      }).catch(() => {
        this.listLoading = false;
      });
    },
    preview(row) {
      this.previewVisible = true
      this.$nextTick(() => {
        this.$refs.menuManage.init(row);
      })
    },
    addOrUpdateHandle(id) {
      this.formVisible = true;
      this.$nextTick(() => {
        this.$refs.Form.init(id);
      });
    },
    handleDel(id) {
      this.$confirm(this.$t("common.delTip"), this.$t("common.tipTitle"), {
        type: "warning",
      }).then(() => {
        delSystem(id).then((res) => {
          this.$message({
            type: "success",
            message: res.msg,
            duration: 1500,
            onClose: () => {
              this.initData();
            },
          });
        });
      }).catch(() => { });
    },
  }
};
