import { getDepartmentSelectorByAuth } from '@/api/permission/department'
import { getPositionList, delPosition } from '@/api/permission/position'
import Form from './Form'
import Diagram from '@/views/permission/user/Diagram'
import UserRelationList from './userRelation'
import CheckPermissions from '@/components/CheckPermissions'
export default {
  name: 'permission-position',
  components: { Form, UserRelationList, Diagram, CheckPermissions },
  data() {
    return {
      typeOptions: [],
      options: [
        { fullName: '启用', id: 1 },
        { fullName: '禁用', id: 0 },
      ],
      treeData: [],
      tableData: [],
      treeLoading: false,
      listLoading: false,
      authorizeFormVisible: false,
      userRelationListVisible: false,
      organizeIdTree: [],
      listQuery: {
        organizeId: '',
        keyword: '',
        type: '',
        enabledMark: '',
        currentPage: 1,
        pageSize: 20
      },
      defaultProps: {
        children: 'children',
        label: 'fullName'
      },
      total: 0,
      diagramVisible: false,
      formVisible: false,
      expands: true,
      refreshTree: true,
      filterText: '',
      checkPermissionsVisible: false
    }
  },
  watch: {
    filterText(val) {
      this.$refs.treeBox.filter(val)
    }
  },
  created() {
    this.getOrganizeList(true)
  },
  methods: {
    checkPermissions(item) {
      this.checkPermissionsVisible = true
      this.$nextTick(() => {
        item = { ...item, type: 'position' }
        this.$refs.checkPermissions.init(item, 2)
      })
    },
    showDiagram() {
      this.diagramVisible = true
      this.$nextTick(() => {
        this.$refs.Diagram.init()
      })
    },
    toggleExpand(expands) {
      this.filterText = ''
      this.refreshTree = false
      this.expands = expands
      this.$nextTick(() => {
        this.refreshTree = true
        this.$nextTick(() => {
          this.$refs.treeBox.setCurrentKey(this.companyId)
        })
      })
    },
    filterNode(value, data) {
      if (!value) return true;
      return data.fullName.indexOf(value) !== -1;
    },
    getOrganizeList(isInit) {
      this.filterText = ''
      this.treeLoading = true
      getDepartmentSelectorByAuth().then(res => {
        this.treeData = res.data.list
        this.$nextTick(() => {
          this.treeLoading = false
          if (isInit) this.initData()
        })
      }).catch(() => {
        this.treeLoading = false
      })
    },
    initData() {
      this.listLoading = true
      // 获取岗位类型
      this.$store.dispatch('base/getDictionaryData', { sort: 'PositionType' }).then(res => {
        console.log(res, 98)
        this.typeOptions = res
      })
      getPositionList(this.listQuery).then(res => {
        this.tableData = res.data.list
        this.total = res.data.pagination.total
        this.listLoading = false
      }).catch(() => {
        this.listLoading = false
      })
    },
    search() {
      this.listQuery = {
        ...this.listQuery,
        currentPage: 1,
        pageSize: 20
      }
      this.initData()
    },
    reset() {
      this.listQuery.keyword = ''
      this.listQuery.type = ''
      this.listQuery.enabledMark = ''
      this.search()
    },
    handleNodeClick(data, node) {
      if (this.listQuery.organizeId === data.id) return
      this.listQuery.organizeId = data.id
      const nodePath = this.getNodePath(node)
      this.organizeIdTree = nodePath.map(o => o.id)
      this.reset()
    },
    getNodePath(node) {
      let fullPath = []
      const loop = (node) => {
        if (node.level) fullPath.unshift(node.data)
        if (node.parent) loop(node.parent)
      }
      loop(node)
      return fullPath
    },
    addOrUpdateHandle(id) {
      this.formVisible = true
      this.$nextTick(() => {
        this.$refs.Form.init(id, this.listQuery.organizeId, this.organizeIdTree)
      })
    },
    removeUserRelationList(isRefresh) {
      this.userRelationListVisible = false
      if (isRefresh) {
        this.keyword = ''
        this.initData()
      }
    },
    removeAuthorizeForm(isRefresh) {
      this.authorizeFormVisible = false
      if (isRefresh) {
        this.keyword = ''
        this.initData()
      }
    },
    handleDel(id) {
      this.$confirm(this.$t('common.delTip'), this.$t('common.tipTitle'), {
        type: 'warning'
      }).then(() => {
        delPosition(id).then(res => {
          this.$message({
            type: 'success',
            message: res.msg,
            duration: 1500,
            onClose: () => {
              this.initData()
            }
          })
        })
      }).catch(() => { })
    },
    handleUserRelation(id, fullName) {
      this.userRelationListVisible = true
      this.$nextTick(() => {
        this.$refs.UserRelationList.init(id, fullName)
      })
    }
  }
}
