import { getInfo } from '@/api/systemData/interfaceOauth.js'
import VerifySignatureInfo from './VerifySignatureInfo'
export default {
  name: 'Preview',
  components: { VerifySignatureInfo },
  data() {
    return {
      btnLoading: false,
      verifySignatureVisible: false,
      dataForm: {
        sortCode: 0,
        enabledMark: 1,
      },
      dataRule: {
        appId: [
          {
            required: true,
            message: '名称不能为空',
            trigger: 'blur'
          }
        ],
        appName: [
          {
            required: true,
            message: '应用名称不能为空',
            trigger: 'blur'
          }
        ],
        appSecret: [
          {
            required: true,
            message: 'appSecret不能为空',
            trigger: 'blur'
          }
        ],
      },
      listLoading: false,
      listLoading2: false,
      tableList: [],
      userList: [],
      tenantId: '',
      options: [
        { label: '字符串', value: 'varchar' },
        { label: '整型', value: 'int' },
        { label: '日期时间', value: 'datetime' },
        { label: '浮点', value: 'decimal' },
        { label: '长整型', value: 'bigint' },
        { label: '文本', value: 'text' }
      ],
      activeName: 'first'
    }
  },
  methods: {
    goBack(isRefresh) {
      this.$emit('close', isRefresh)
    },
    init(id) {
      this.activeName = 'first'
      if (id) {
        this.initData(id)
      }
    },
    initData(id) {
      this.dataForm.id = id
      this.setInterfaceData()
    },
    setInterfaceData() {
      getInfo(this.dataForm.id).then(res => {
        if (res.data) {
          this.dataForm = res.data
          this.tenantId = this.dataForm.tenantId ? this.dataForm.tenantId : ''
        }
        if (res.data.list) {
          let arr = []
          res.data.list.forEach(item => {
            item.url = `${this.define.comUrl}/api/system/DataInterface/${item.id}/Actions/Response` + (this.tenantId ? '?tenantId=' + this.tenantId : '')
            if (item.parameterJson) {
              item.paramList = [...JSON.parse(item.parameterJson)]
            }
            if (item.paramList && item.paramList.length) {
              item.paramList.forEach(elem => {
                this.options.forEach(elm2 => {
                  if (elm2.value == elem.dataType) {
                    elem.dataType = elm2.label
                  }
                })

              })
            }
          })
          this.tableList = res.data.list
        }
        this.userList = res.data.userList || []
      })
    },
    showVerify() {
      this.verifySignatureVisible = true
      this.$nextTick(() => {
        this.$refs.VerifySignatureInfo.init()
      })
    },
    cellDblclick(row, column, cell, event) {
      if (event.type == "dblclick") {
        var save = function (e) {
          e.clipboardData.setData("text/plain", event.target.innerText)
          e.preventDefault()
        }
        document.addEventListener("copy", save)
        document.execCommand("copy")
        document.removeEventListener("copy", save)
      }
      this.$message({ message: '复制成功', type: 'success' })
    },
  }
}
