import { DataModelData, DataModelFieldList } from '@/api/systemData/dataModel'
export default {
  data() {
    return {
      visible: false,
      options: [],
      queryJson: {
        field: '',
        keyword: ""
      },
      total: 0,
      listLoading: true,
      listQuery: {
        currentPage: 1,
        pageSize: 20,
        sort: 'desc'
      },
      dataBase: '',
      table: '',
      list: []
    }
  },
  methods: {
    goBack() {
      this.$emit('close')
    },
    reset() {
      this.queryJson.keyword = ''
      this.search()
    },
    search() {
      this.listQuery = {
        currentPage: 1,
        pageSize: 20,
        sort: 'desc'
      }
      this.initData()
    },
    cellDblclick(row, column, cell, event) {
      if (event.type == "dblclick") {
        var save = function (e) {
          e.clipboardData.setData("text/plain", event.target.innerText)
          e.preventDefault()
        }
        document.addEventListener("copy", save)
        document.execCommand("copy")
        document.removeEventListener("copy", save)
      }
      this.$message({ message: '复制成功', type: 'success' })
    },
    init(dataBase, table) {
      if (!dataBase || !table) {
        this.$emit('close')
        return
      }
      this.dataBase = dataBase
      this.table = table
      this.$nextTick(() => {
        DataModelFieldList(dataBase, table).then(res => {
          this.options = res.data.list
          this.queryJson.field = this.options[0].field
          this.reset()
        })
      })
    },
    initData() {
      let query = {
        ...this.listQuery,
        ...this.queryJson
      }
      this.listLoading = true
      this.list = []
      DataModelData(this.dataBase, this.table, query).then(res => {
        this.list = res.data.list
        this.total = res.data.pagination.total
        this.listLoading = false
      }).catch(() => {
        this.listLoading = false
      })
    }
  }
}
