import { getAmountChinese, thousandsFormat } from "@/components/Generator/utils/index"

export default {
  name: 'JnpfInputNumber',
  components: {},
  props: {
    controlsPosition: {
      type: String,
      default: ''
    },
    controls: {
      type: Boolean,
      default: true
    },
    value: {
      type: [Number, String],
      default: undefined
    },
    max: {
      type: Number,
      default: Infinity
    },
    min: {
      type: Number,
      default: -Infinity
    },
    step: {
      type: Number,
      default: 1
    },
    precision: {
      type: Number,
      default: undefined
    },
    disabled: {
      type: Boolean,
      default: false
    },
    thousands: {
      type: Boolean,
      default: false
    },
    isAmountChinese: {
      type: Boolean,
      default: false
    },
    addonBefore: {
      type: String,
      default: ''
    },
    placeholder: {
      type: String,
      default: '数字文本'
    },
    addonAfter: {
      type: String,
      default: ''
    },
    readonly: {
      type: Boolean,
      default: false
    }
  },
  data() {
    return {
      innerValue: undefined,
      amountChineseName: '',
      key2: ''
    }
  },
  watch: {
    innerValue: {
      handler(val) {
        this.amountChinese(val)
      },
      deep: true
    },
    value: {
      handler(val) {
        let value = val ? val : val === 0 ? 0 : undefined
        this.innerValue = value
        this.amountChinese(val)
      },
      deep: true,
      immediate: true
    },
    isAmountChinese() {
      this.amountChinese(this.value)
    },
    thousands(val) {
      this.key2 = +new Date()
    }
  },
  directives: {
    thousands: {
      // 被绑定元素插入父节点时调用
      inserted: (el, binding, vnode) => {
        let precision = vnode.child.precision
        let thousands = vnode.context.thousands
        // 获取input节点
        if (el.tagName.toLocaleUpperCase() !== 'INPUT') {
          el = el.getElementsByTagName('input')[0]
        }
        if (thousands) {
          // 千分位
          el.value = vnode.context.format_number_thousandth(el.value)
          // 聚焦转化为数字格式（去除千分位）
          el.onfocus = e => {
            if (el.value) {
              let a = el.value.replace(/,/g, '') //去除千分号的','
              el.value = Number(parseFloat(a))
              if (precision) el.value = Number(el.value).toFixed(precision)
            }
          }
          el.onblur = e => {
            el.value = vnode.context.format_number_thousandth(el.value)
          }
        }
      },
      componentUpdated: (el, binding, vnode) => {
        // 聚焦转化为数字格式（去除千分位）
        el.focus()
        let precision = vnode.child.precision
        let thousands = vnode.context.thousands
        if (thousands) {
          if (el.tagName.toLocaleUpperCase() !== 'INPUT') {
            el = el.getElementsByTagName('input')[0]
          }
          el.onblur = e => {
            if (el.value) {
              if (precision) el.value = Number(el.value).toFixed(precision)
              el.value = vnode.context.format_number_thousandth(el.value)
            }
          }
        }
      },
      unbind: (el, binding, vnode) => {
        // 聚焦转化为数字格式（去除千分位）
        if (el.tagName.toLocaleUpperCase() !== 'INPUT') {
          el = el.getElementsByTagName('input')[0]
        }
      }
    }
  },
  methods: {
    amountChinese(val) {
      if (!this.isAmountChinese) return
      this.amountChineseName = getAmountChinese(val)
    },
    format_number_thousandth(num) {
      return thousandsFormat(num)
    },
    onblur(event) {
      this.$emit('blur', event)
    },
    change(val) {
      let value = val ? val : val == 0 ? 0 : undefined
      this.$emit('input', value)
      this.$emit('change', value)
    },
  }
}
