import {
  getUserList
} from '@/api/permission/user'
import { removeResizeListener } from 'element-ui/src/utils/resize-event';
export default {
  name: 'useSelect',
  data() {
    return {
      visible: false,
      title: '',
      list: [],
      listQuery: {
        organizeId: '',
        keyword: '',
        currentPage: 1,
        pageSize: 20,
        enabledMark: 1,
      },
      finish: false,
      loading: false
    }
  },
  beforeDestroy() {
    if (this.$el && this.handleResize) removeResizeListener(this.$el, this.handleResize);
  },
  methods: {
    init(id, name) {
      this.visible = true
      this.list = []
      this.title = '查看成员 - ' + name || ''
      this.listQuery.organizeId = id
      this.listQuery.keyword = ''
      this.listQuery.currentPage = 1
      this.finish = false
      this.loading = false
      this.initData()
      this.$nextTick(() => {
        this.bindScroll()
      })
    },
    search() {
      this.list = []
      this.finish = false
      this.loading = false
      this.listQuery.currentPage = 1
      this.listQuery.sort = 'desc'
      this.initData()
    },
    reset() {
      this.listQuery.keyword = ''
      this.search()
    },
    initData() {
      this.loading = true
      getUserList(this.listQuery).then(res => {
        if (res.data.list.length < this.listQuery.pageSize) this.finish = true
        this.list = [...this.list, ...res.data.list]
        this.loading = false
      }).catch(() => {
        this.loading = false
      })
    },
    bindScroll() {
      let _this = this,
        vBody = _this.$refs.list;
      vBody.addEventListener("scroll", function () {
        if (vBody.scrollHeight - vBody.clientHeight - vBody.scrollTop <= 200 && !_this.loading && !_this.finish) {
          _this.listQuery.currentPage += 1
          _this.initData()
        }
      });
    },
    confirm() {
      this.visible = false
    },
    handleResize() {
      this.resetInputWidth();
      if (this.multiple) this.resetInputHeight();
    },
  },
}
