import { DocumentPreviewList } from '@/api/extend/documentPreview'
import Detail from './Detail'
export default {
  components: { Detail },
  name: 'extend-documentPreview',
  data() {
    return {
      activeTab: 'localPreview',
      detailVisible: false,
      keyword: '',
      list: [],
      listLoading: true,
      formVisible: false
    }
  },
  created() {
    this.initData()
  },
  methods: {
    initData() {
      this.listLoading = true
      let query = { keyword: this.keyword }
      DocumentPreviewList(query).then(res => {
        this.list = res.data
        this.listLoading = false
      })
    },
    readInfo(id, name) {
      this.detailVisible = true
      this.$nextTick(() => {
        this.$refs.detail.init(id, name, this.activeTab)
      })
    },
    reset() {
      this.keyword = ''
      this.initData()
    }
  }
}
