import {
  testConfig
} from '@/api/msgCenter/accountConfig'
export default {
  data() {
    return {
      visible: false,
      testForm: {
        testSendEmail: "",
        testEmailTitle: "",
        testEmailContent: ""
      },
      dataForm: {},
      btnLoading: false,
      dataRule: {
        testSendEmail: [
          { required: true, message: '请选择收件人', trigger: 'click' },
        ],
        testEmailTitle: [
          { required: true, message: '请输入邮件标题', trigger: 'blur' },
          { max: 50, message: '编码最多为50个字符！', trigger: 'blur' }
        ],
        testEmailContent: [
          { required: true, message: '请输入邮件内容', trigger: 'blur' }
        ]
      }
    }
  },
  methods: {
    init(data) {
      this.visible = true
      this.dataForm = data
      this.testForm.testSendEmail = '';
      this.testForm.testEmailTitle = '测试';
      this.testForm.testEmailContent = '测试';
      this.$nextTick(() => {
        this.$refs['testForm'].resetFields()
      })
    },
    testSubmit() {
      this.$refs['testForm'].validate((valid) => {
        if (valid) {
          this.btnLoading = true
          let query = {
            ...this.testForm,
            ...this.dataForm,
            type: 2
          }
          query.testSendEmail = [query.testSendEmail]
          testConfig(query, 'testSendMail').then(res => {
            this.$message({
              type: 'success',
              message: res.msg,
              duration: 1000,
              onClose: () => {
                this.btnLoading = false
              }
            });
          }).catch(() => {
            this.btnLoading = false
          })
        }
      })
    }
  }
}
