import {
  HTodo,
  HCommonFunc,
  HTodoList,
  HNotice,
  HEmail,
  HDataBoard,
  HTable,
  HText,
  HImage,
  HCarousel,
  HVideo,
  HSchedule,
  HIframe,
  HBarChart,
  HRankList,
  HLineChart,
  HPieChart,
  HMapChart,
  HRadarChart,
  HTimeAxis,
} from "@/components/VisualPortal"
import CardHeader from "@/components/VisualPortal/CardHeader"
import AddBtn from "./AddBtn"

export default {
  name: 'parser',
  props: ['item', 'activeId', 'showType', 'detailed'],
  components: {
    HTodo,
    HCommonFunc,
    HTodoList,
    HNotice,
    HEmail,
    HDataBoard,
    HTable,
    HText,
    HImage,
    HCarousel,
    HVideo,
    HSchedule,
    HIframe,
    HBarChart,
    HLineChart,
    HPieChart,
    HRadarChart,
    HMapChart,
    HRankList,
    HTimeAxis,
    CardHeader,
    AddBtn
  },
  data() {
    return {
      key: +new Date()
    }
  },
  watch: {
    "item.type": {
      handler(val) {
        this.handleTabClick()
      }
    },
  },
  created() {
    this.$watch(
      () => {
        return this.item['tab-position']
      },
      (val) => {
        this.handleTabClick()
      }
    )
  },
  methods: {
    addComponent(val, index) {
      this.$eventBus.$emit('addComponent', val, this.item, index)
    },
    handleClick(data) {
      this.$eventBus.$emit('handlerActive', data)
    },
    handleTabClick() {
      const active = this.item.active
      const list = this.item.children;
      for (let i = 0; i < list.length; i++) {
        if (list[i].name === active && list[i].children && list[i].children.length) {
          list[i].children.map(ele => {
            this.$eventBus.$emit('eChart' + ele.i)
          });
        }
      }
    },
    handleRemoveItem(i, parent) {
      parent.splice(i, 1)
    }
  },
}
