import { getOrderList, getProductEntry, delOrder } from '@/api/extend/saleOrder'
import Form from './Form'
export default {
  name: 'extend-saleOrder',
  components: { Form },
  data() {
    return {
      list: [],
      total: 0,
      listLoading: true,
      showAll: false,
      query: {
        keyword: '',
        code: '',
        customerName: '',
      },
      listQuery: {
        currentPage: 1,
        pageSize: 20,
        sort: 'desc',
        sidx: ''
      },
      formVisible: false,
      multipleSelection: [],
      activeId: '',
      productEntryList: [],
      childrenList: [],
    }
  },
  created() {
    this.initData()
  },
  methods: {
    search() {
      this.listQuery = {
        currentPage: 1,
        pageSize: 20,
        sort: 'desc',
        sidx: ''
      }
      this.productEntryList = []
      this.childrenList = []
      this.initData()
    },
    initData() {
      this.listLoading = true
      const query = {
        ...this.listQuery,
        ...this.query
      }
      getOrderList(query).then(res => {
        this.list = res.data.list
        this.total = res.data.pagination.total
        this.listLoading = false
      })
    },
    handleDel() {
      if (this.multipleSelection.length != 1) {
        this.$message.error('请只选择一条数据')
        return
      }
      const id = this.multipleSelection[0].id
      this.$confirm('此操作将永久删除该数据, 是否继续？', '提示', {
        type: 'warning'
      }).then(() => {
        delOrder(id).then(res => {
          this.initData()
          this.productEntryList = []
          this.childrenList = []
          this.$message({
            type: 'success',
            message: res.msg
          });
        })
      }).catch(() => { });
    },
    rowClick(item) {
      if (this.activeId === item.id) return
      this.activeId = item.id
      getProductEntry(this.activeId).then(res => {
        this.productEntryList = res.data.list
      })
    },
    rowClick1(item) {
      this.childrenList = item.dataList || []
    },
    handleEdit() {
      if (this.multipleSelection.length != 1) {
        this.$message.error('请只选择一条数据')
        return
      }
      const id = this.multipleSelection[0].id
      this.handleAdd(id)
    },
    handleChange(val) {
      this.multipleSelection = val
    },
    handleAdd(id) {
      this.formVisible = true
      this.$nextTick(() => {
        this.$refs.form.init(id)
      })
    },
    closeForm(isRefresh) {
      this.formVisible = false
      if (isRefresh) this.refresh()
    },
    reset() {
      this.query = {
        keyword: '',
        code: '',
        customerName: '',
      }
      this.search()
    },
    refresh() {
      this.productEntryList = []
      this.childrenList = []
      this.initData()
    }
  }
}
