import { getLogList, delLog, batchDelLog } from '@/api/system/log'
import Form from './Form'
export default {
  name: 'system-log',
  components: { Form },
  data() {
    return {
      btnLoading: false,
      showAll: false,
      formVisible: false,
      listLoading: true,
      activeName: '1',
      loginLogData: [],
      errorLogData: [],
      requestLogData: [],
      operationLogData: [],
      multipleSelection: [],
      total: 0,
      listQuery: {
        keyword: '',
        startTime: '',
        loginType: '',
        loginMark: '',
        endTime: '',
        sort: 'desc',
        currentPage: 1,
        pageSize: 20,
        requestMethod: ''
      },
      pickerVal: []
    }
  },
  created() {
    this.initData()
  },
  methods: {
    initData() {
      const activeId = this.activeName
      this.listLoading = true
      this.listQuery.category = activeId
      getLogList(this.listQuery).then(res => {
        if (activeId === '1') this.loginLogData = res.data.list
        if (activeId === '3') this.operationLogData = res.data.list
        if (activeId === '4') this.errorLogData = res.data.list
        if (activeId === '5') this.requestLogData = res.data.list
        this.total = res.data.pagination.total
        this.listLoading = false
        this.btnLoading = false
      }).catch(() => {
        this.listLoading = false
        this.btnLoading = false
      })
      return
    },
    handleTabClick() {
      this.reset()
    },
    goDetail(data, type) {
      this.formVisible = true
      this.$nextTick(() => {
        this.$refs.Form.init(data, type)
      })
    },
    handleSelectionChange(val) {
      const res = val.map(item => item.id)
      this.multipleSelection = res
    },
    handleDel() {
      if (!this.multipleSelection.length) {
        this.$message({
          type: 'error',
          message: '请选择一条数据',
          duration: 1500,
        })
        return
      }
      const data = {
        ids: this.multipleSelection
      }
      this.$confirm('您确定要删除这些数据吗, 是否继续?', '提示', {
        type: 'warning'
      }).then(() => {
        this.btnLoading = true
        delLog(data).then(res => {
          this.$message({
            type: 'success',
            message: res.msg,
            duration: 1500,
            onClose: () => {
              this.initData()
            }
          })
        }).catch(() => { this.btnLoading = false })
      }).catch(() => { this.btnLoading = false })
    },
    batchDel() {
      this.$confirm('此操作会将所有日志删除，是否继续?', '提示', {
        type: 'warning'
      }).then(() => {
        this.btnLoading = true
        batchDelLog(this.activeName).then(res => {
          this.$message({
            type: 'success',
            message: res.msg,
            duration: 1500,
            onClose: () => {
              this.initData()
            }
          })
        }).catch(() => { this.btnLoading = false })
      }).catch(() => { this.btnLoading = false })
    },
    search() {
      this.btnLoading = true
      this.listQuery.currentPage = 1
      if (this.pickerVal && this.pickerVal.length) {
        this.listQuery.startTime = this.pickerVal[0]
        this.listQuery.endTime = this.pickerVal[1]
      } else {
        this.listQuery.startTime = ''
        this.listQuery.endTime = ''
      }
      this.initData()
    },
    reset() {
      this.pickerVal = []
      this.listQuery.keyword = ''
      this.listQuery.loginMark = ''
      this.listQuery.loginType = ''
      this.listQuery.requestMethod = ''
      this.listQuery.pickerVal = []
      this.search()
    }
  }
}
