export default {
  components: {},
  props: {
  },
  data() {
    return {
      visible: false,
      resultInfo: '',
      separator: '|',
      options: []
    }
  },
  computed: {
  },
  methods: {
    init(options) {
      this.options = options || []
      this.analysis()
      this.visible = true
    },
    analysis() {
      this.resultInfo = ''
      if (this.options.length > 0) {
        this.options.forEach((opt) => {
          if (opt.id === opt.fullName) {
            this.resultInfo += opt.fullName + this.separator + opt.id + '\n'
          } else {
            this.resultInfo += opt.fullName + this.separator + opt.id + '\n'
          }
        })
      }
    },
    handleSure() {
      let lineArray = this.resultInfo.split('\n')
      if (lineArray.length > 0) {
        this.options = []
        lineArray.forEach((optLine) => {
          if (!!optLine && !!optLine.trim()) {
            if (optLine.indexOf(this.separator) !== -1) {
              this.options.push({
                id: optLine.split(this.separator)[1],
                fullName: optLine.split(this.separator)[0]
              })
            } else {
              this.options.push({
                id: optLine,
                fullName: optLine
              })
            }
          }
        })
      } else {
        this.options = []
      }
      this.$emit('change', this.options)
      this.visible = false
    },
  }
}
