import { getMsgTemplateList } from '@/api/msgCenter/msgTemplate'
import { getConfigList } from '@/api/msgCenter/accountConfig'
export default {
  name: 'sendConfig-TemplateDialog',
  props: {
    value: {
      default: ''
    },
    title: {
      type: String,
      default: ''
    },
    clearable: {
      type: Boolean,
      default: true
    },
    disabled: {
      type: Boolean,
      default: false
    },
    type: {
      type: Number,
      default: 1
    },
    messageType: {
      type: [String, Number],
      default: 0
    },
    messageSource: {
      type: [String, Number],
      default: ''
    }
  },
  model: {
    prop: 'value',
    event: 'input'
  },
  data() {
    return {
      list: [],
      innerValue: '',
      listQuery: {
        keyword: '',
        currentPage: 1,
        pageSize: 20,
        enabledMark: 1,
        messageType: 1
      },
      total: 0,
      checked: '',
      checkedRow: {},
      listLoading: false,
      inputHovering: false,
      visible: false
    }
  },
  computed: {
    showClose() {
      let hasValue = this.value !== undefined && this.value !== null && this.value !== '';
      let criteria = this.clearable &&
        !this.disabled &&
        this.inputHovering &&
        hasValue;
      return criteria;
    }
  },
  methods: {
    initData() {
      this.listLoading = true
      if (this.type == 2) this.listQuery.type = this.messageType
      if (this.type != 2) {
        this.listQuery.messageType = this.messageType
        this.listQuery.messageSource = this.messageSource
      }
      const formMethod = this.type == 2 ? getConfigList : getMsgTemplateList
      formMethod(this.listQuery).then(res => {
        this.list = res.data.list
        this.total = res.data.pagination.total
        this.listLoading = false
        if (this.value) this.checkedRow = this.list.find(ele => ele.id == this.value) || {}
      }).catch(() => { this.listLoading = false })
    },
    openDialog() {
      if (this.disabled) return
      if (!this.messageType) return this.$message({ message: '请选择消息类型', type: 'warning' });
      this.checked = this.value
      this.visible = true
      this.reset()
    },
    reset() {
      this.listQuery.keyword = ''
      this.search()
    },
    search() {
      this.listQuery.currentPage = 1
      this.listQuery.pageSize = 20
      this.initData()
    },
    clear() {
      this.checked = ''
      this.checkedRow = {}
      this.$emit('input', this.checked)
      this.$emit('change', this.checked, this.checkedRow)
    },
    select() {
      if (!this.checked) return
      this.$emit('input', this.checked)
      this.$emit('change', this.checked, this.checkedRow)
      this.visible = false
    },
    rowClick(row) {
      this.checked = row.id
      this.checkedRow = row
    }
  }
}
