import { mapGetters } from 'vuex'
export default {
  name: 'lockScreen',
  data() {
    return {
      password: '',
      loading: false
    }
  },
  created() {
    var _this = this
    document.onkeydown = function (e) {
      var key = window.event.keyCode
      if (key === 13) {
        _this.handleLogin()
      }
    }
  },
  mounted() { },
  computed: {
    ...mapGetters(['userInfo']),
  },
  destroyed() {
    document.onkeydown = function (e) {
      var key = window.event.keyCode
      if (key === 13) {

      }
    }
  },
  methods: {
    handleLogout() {
      this.$confirm('您确定要退出应用程序吗?', '提示', {
        type: 'warning'
      }).then(() => {
        this.$store.dispatch('user/logout').then(() => {
          this.$router.push({ path: '/login' })
        })
      }).catch(() => {
        return false
      })
    },
    handleLogin() {
      this.loading = true
      if (!this.password) {
        this.$message({
          message: '请输入解锁密码',
          type: 'error'
        })
        this.loading = false
        return
      }
      this.$store
        .dispatch('user/unlock', {
          password: this.password,
          account: this.userInfo.userAccount
        })
        .then(() => {
          this.$router.go(-1);
          this.loading = false
        })
        .catch(() => {
          this.loading = false
        })
    }
  },
  components: {}
}
