import {
  createConfig,
  updateConfig,
  getConfigDetail
} from '@/api/msgCenter/accountConfig'
import TestForm from './SendTestForm'
export default {
  components: {
    TestForm
  },
  data() {
    return {
      TestFormVisible: false,
      visible: false,
      formLoading: false,
      btnLoading: false,
      dataForm: {
        fullName: '',
        enCode: '',
        addressorName: '',
        smtpServer: '',
        smtpPort: '25',
        sslLink: 0,
        smtpUser: '',
        smtpPassword: '',
        sortCode: 0,
        enabledMark: 1,
        description: ''
      },
      dataRule: {
        fullName: [
          { required: true, message: '请输入名称', trigger: 'blur' },
          { max: 50, message: '名称最多为50个字符！', trigger: 'blur' }
        ],
        enCode: [
          { required: true, message: '请输入编码', trigger: 'blur' },
          { max: 50, message: '编码最多为50个字符！', trigger: 'blur' },
          { validator: this.formValidate('enCode'), trigger: 'blur' },
        ],
        addressorName: [
          { required: true, message: '请输入发件人昵称', trigger: 'blur' }
        ],
        smtpServer: [
          { required: true, message: '请输入SMTP服务器', trigger: 'blur' }
        ],
        smtpPort: [
          { required: true, message: '请输入SMTP端口', trigger: 'blur' }
        ],
        smtpUser: [
          { required: true, message: '请输入SMTP用户名', trigger: 'blur' }
        ],
        smtpPassword: [
          { required: true, message: '请输入SMTP密码', trigger: 'blur' }
        ]
      }
    }
  },
  methods: {
    init(id) {
      this.dataForm.id = id || ''
      this.visible = true
      this.formLoading = true
      this.$nextTick(() => {
        this.$refs['dataForm'].resetFields()
        if (this.dataForm.id) {
          getConfigDetail(this.dataForm.id).then(res => {
            this.dataForm = res.data
          })
        }
      })
      this.formLoading = false
    },
    sendTest() {
      this.$refs['dataForm'].validate((valid) => {
        if (valid) {
          let query = {
            ...this.dataForm,
            type: 2
          }
          this.TestFormVisible = true
          this.$nextTick(() => {
            this.$refs.TestForm.init(query)
          })
        }
      })

    },
    dataFormSubmit() {
      this.$refs['dataForm'].validate((valid) => {
        if (valid) {
          let query = {
            ...this.dataForm,
            type: 2
          }
          this.btnLoading = true
          const formMethod = query.id ? updateConfig : createConfig
          formMethod(query).then(res => {
            this.$message({
              message: res.msg,
              type: 'success',
              duration: 1500,
              onClose: () => {
                this.visible = false
                this.btnLoading = false
                this.$emit('refreshDataList')
              }
            })
          }).catch(() => {
            this.btnLoading = false
          })
        }
      })
    }
  }
}
