import { update } from '@/api/onlineDev/integrate'
import Process from "@/components/integrationProcess"

export default {
  components: { Process },
  data() {
    return {
      visible: false,
      activeStep: 0,
      dataForm: {
        id: '',
        templateJson: null
      },
      btnLoading: false,
      templateJson: {},
      loading: false,
      title: '',
      key: + new Date(),
    }
  },
  methods: {
    init(item) {
      this.dataForm.id = item.id || ''
      this.key = + new Date()
      this.visible = true
      this.loading = true
      this.$nextTick(() => {
        if (this.dataForm.id) {
          this.dataForm = item
          this.title = this.dataForm.fullName
          this.loading = false
        }
      })
    },
    dataFormSubmit() {
      this.$refs.processDesign && this.$refs.processDesign.getData().then(res => {
        this.dataForm = res.data
        this.dataForm.templateJson = JSON.stringify(res.data.templateJson)
        this.submit()
      }).catch(err => { err.msg && this.$message.warning(err.msg) })
    },
    submit() {
      this.btnLoading = true
      update(this.dataForm).then((res) => {
        this.$message({
          message: res.msg,
          type: 'success',
          duration: 1500,
          onClose: () => {
            this.btnLoading = false
            this.closeDialog(true)
          }
        })
      }).catch(() => { this.btnLoading = false })
    },
    closeDialog(isRefresh) {
      this.visible = false
      this.$emit('close', isRefresh)
    },
  }
}
