import { getAuthorizeList, Delete, CopyPermissions } from '@/api/permission/authorize'
import Form from './Form'
import userTransfer from './permissionGroup'
import AuthorizeForm from './AuthorizeForm'
import AuthorizePortal from './AuthorizePortal'
export default {
  name: 'onlineDev-visualPortal',
  components: { Form, userTransfer, AuthorizeForm, AuthorizePortal },
  data() {
    return {
      list: [],
      keyword: '',
      enabledMark: '',
      listQuery: {
        currentPage: 1,
        pageSize: 20,
        sort: 'desc',
        sidx: ''
      },
      total: 0,
      drawerVisible: false,
      listLoading: false,
      formVisible: false,
      selectedData: [],
      authorizeFormVisible: false,
      authorizePortalVisible: false,
      authorizeId: '',
      userVisible: false,
      options: [
        { fullName: '启用', id: 1 },
        { fullName: '禁用', id: 0 },
      ]
    }
  },
  created() {
    this.initData()
  },
  methods: {
    reset() {
      this.keyword = ''
      this.enabledMark = ''
      this.search()
    },
    search() {
      this.listQuery = {
        currentPage: 1,
        pageSize: 20,
        sort: 'desc',
        sidx: ''
      }
      this.initData()
    },
    initData() {
      this.listLoading = true
      let query = {
        ...this.listQuery,
        keyword: this.keyword,
        enabledMark: this.enabledMark
      }
      getAuthorizeList(query).then(res => {
        this.list = res.data.list
        this.total = res.data.pagination.total
        this.listLoading = false
      })
    },
    copyAuthorize(id) {
      this.$confirm('您确定要复制该功能权限, 是否继续?', this.$t('common.tipTitle'), {
        type: 'warning'
      }).then(() => {
        CopyPermissions(id).then(res => {
          this.$message({
            type: 'success',
            message: res.msg,
            duration: 1000,
            onClose: () => {
              this.initData()
            }
          });
        })
      }).catch(() => { });
    },
    handleDel(id) {
      this.$confirm(this.$t('common.delTip'), this.$t('common.tipTitle'), {
        type: 'warning'
      }).then(() => {
        Delete(id).then(res => {
          this.$message({
            type: 'success',
            message: res.msg,
            duration: 1000,
            onClose: () => {
              this.initData()
            }
          });
        })
      }).catch(() => { });
    },
    addOrUpdateHandle(id) {
      this.dialogVisible = false
      this.formVisible = true
      this.$nextTick(() => {
        this.$refs.form.init(id)
      })
    },
    closeForm(isRefresh) {
      this.formVisible = false
      if (isRefresh) this.initData()
    },
    closeUser(isRefresh) {
      this.userVisible = false
      if (isRefresh) this.initData()
    },
    setUserAuthorize(id) {
      this.authorizeId = id || ''
      this.userVisible = true
      this.$nextTick(() => {
        this.$refs.transfer.init()
      })
    },
    handleAuthorize(id, fullName) {
      this.authorizeFormVisible = true
      fullName = fullName + '的权限授权'
      this.$nextTick(() => {
        this.$refs.AuthorizeForm.init(id, fullName, 'Role')
      })
    },
    handleAuthorizePortal(id, fullName) {
      this.authorizePortalVisible = true
      fullName = fullName + '的门户权限'
      this.$nextTick(() => {
        this.$refs.AuthorizePortal.init(id, fullName)
      })
    },
    removeAuthorizeForm(isRefresh) {
      this.authorizeFormVisible = false
      this.authorizePortalVisible = false
      if (isRefresh) {
        this.initData()
      }
    },
  }
}
